import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';
import Footer from '../components/Footer';
import ProductCard from '../components/ProductCard';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [similarProducts, setSimilarProducts] = useState([]);
  const [selectedImage, setSelectedImage] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [selectedSize, setSelectedSize] = useState('');

  const sizeCategories = ['T-shirt', 'T-shirts', 'Shirt', 'Shirts', 'Jacket', 'Jackets'];

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/get/${id}`);
      setProduct(res.data);
      setSelectedImage(res.data.imageUrl);
      const categoryName = typeof res.data.category === 'object' ? res.data.category.name : res.data.category;
      if (categoryName) fetchSimilarProducts(categoryName);
    } catch (err) {
      setError('❌ Product not found');
    }
  };

  const fetchSimilarProducts = async (categoryName) => {
    try {
      const res = await api.get(`/v1/products/category/byname?name=${encodeURIComponent(categoryName)}`);
      const filtered = res.data.filter(p => p.id !== Number(id));
      setSimilarProducts(filtered);
    } catch (err) {
      console.error('Similar fetch failed:', err);
    }
  };

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/login');

    try {
      await api.post('/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      });
      setMessage('✔️ Added to cart');
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      setMessage('❌ Failed to add to cart.');
    }
  };

  const handleBuyNow = () => {
    navigate('/checkout', { state: { product } });
  };

  if (error) return <div style={{ padding: '20px', color: 'red' }}>{error}</div>;
  if (!product) return <div style={{ padding: '20px' }}>Loading...</div>;

  const showSizes = sizeCategories.includes(product.category);

  return (
    <div style={{ padding: '40px 30px' }}>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '40px' }}>
        {/* Image Preview */}
        <div>
          <img src={selectedImage || '/placeholder.png'} alt={product.name} style={{ width: 300, height: 300, borderRadius: '8px', objectFit: 'cover' }} />
          <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
            <img
              src={product.imageUrl}
              alt="thumb"
              onClick={() => setSelectedImage(product.imageUrl)}
              style={{ width: 60, height: 60, objectFit: 'cover', border: selectedImage === product.imageUrl ? '2px solid #ff3f6c' : '1px solid #ccc', cursor: 'pointer' }}
            />
            {/* You can add more thumbnails if you support multiple images */}
          </div>
        </div>

        {/* Product Details */}
        <div style={{ flex: 1 }}>
          <h2>{product.name}</h2>
          <p><strong>Price:</strong> ₹{product.price}</p>
          <p><strong>Brand:</strong> {product.brand}</p>
          <p><strong>Description:</strong> {product.description}</p>
          <p><strong>Category:</strong> {product.category}</p>

          {/* Size selector for specific categories */}
          {showSizes && (
            <div>
              <p><strong>Select Size:</strong></p>
              {['S', 'M', 'L', 'XL'].map(size => (
                <button
                  key={size}
                  onClick={() => setSelectedSize(size)}
                  style={{
                    margin: '5px',
                    padding: '8px 16px',
                    borderRadius: '4px',
                    border: selectedSize === size ? '2px solid #ff3f6c' : '1px solid #ccc',
                    backgroundColor: selectedSize === size ? '#ffeef1' : '#fff',
                    cursor: 'pointer'
                  }}
                >
                  {size}
                </button>
              ))}
            </div>
          )}

          {/* Coupons Section */}
          <div style={{ backgroundColor: '#f9f9f9', padding: '10px 15px', borderRadius: '6px', marginTop: '20px' }}>
            <p style={{ fontWeight: 'bold', color: '#43a047' }}>💸 Coupons for You</p>
            <ul style={{ marginLeft: '20px', fontSize: '14px' }}>
              <li>🎁 Extra ₹15 off on 2+ items</li>
              <li>💳 5% cashback on Flipkart Axis Card</li>
              <li>📦 10% off on orders over ₹5000</li>
            </ul>
          </div>

          {/* Actions */}
          <div style={{ marginTop: '20px' }}>
            <button
              onClick={handleAddToCart}
              style={{ backgroundColor: '#ff3f6c', color: '#fff', padding: '10px 20px', marginRight: '10px', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              🛒 Add to Cart
            </button>
            <button
              onClick={handleBuyNow}
              style={{ backgroundColor: '#ff9f00', color: '#fff', padding: '10px 20px', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
            >
              ⚡ Buy Now
            </button>
            {message && <p style={{ marginTop: '10px', color: message.includes('✔️') ? 'green' : 'red' }}>{message}</p>}
          </div>
        </div>
      </div>

      {/* Similar Products */}
      <div style={{ marginTop: '60px' }}>
        <h3>Similar Products</h3>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', marginTop: '20px' }}>
          {similarProducts.length > 0 ? (
            similarProducts.map(p => <ProductCard key={p.id} product={p} />)
          ) : (
            <p>No similar products found.</p>
          )}
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default ProductDetail;
