import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import NavBar from './components/NavBar';
import AdminNavBar from './components/AdminNavBar';
import Home from './pages/Home';
import ProductDetail from './pages/ProductDetail';
import Login from './pages/Login';
import Register from './pages/Register';
import Cart from './pages/Cart';
import Checkout from './pages/Checkout';
import CategoryProducts from './pages/CategoryProducts';
import OrderDetails from './pages/OrderDetails';
import MyOrders from './pages/MyOrders';
import WishlistPage from './pages/WishlistPage';
import ContactUs from './pages/ContactUs';
import CategorySearchPage from './pages/CategorySearchPage';
import AdminDashboard from './pages/AdminDashboard';

import './App.css';

function App() {
  const [authState, setAuthState] = useState({
    isAuthenticated: !!localStorage.getItem('token'),
    isAdmin: localStorage.getItem('role') === 'ADMIN'
  });

  // Listen for storage changes to handle logout across components
  useEffect(() => {
    const handleStorageChange = () => {
      console.log('App: Storage changed, updating auth state');
      setAuthState({
        isAuthenticated: !!localStorage.getItem('token'),
        isAdmin: localStorage.getItem('role') === 'ADMIN'
      });
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  return (
    <div>
      {authState.isAdmin ? <AdminNavBar /> : <NavBar />}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/product/:id" element={<ProductDetail />} />
        <Route path="/login" element={authState.isAuthenticated ? <Navigate to={authState.isAdmin ? "/admin-dashboard" : "/"} replace /> : <Login />} />
        <Route path="/register" element={authState.isAuthenticated ? <Navigate to={authState.isAdmin ? "/admin-dashboard" : "/"} replace /> : <Register />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/checkout" element={<Checkout />} />
        <Route path="/category/:name" element={<CategoryProducts />} />
        <Route path="/order/:orderId" element={<OrderDetails />} />
        <Route path="/myorders" element={<MyOrders />} />
        <Route path="/wishlist" element={<WishlistPage />} />
        <Route path="/contact" element={<ContactUs />} /> 
        <Route path="/category/byname" element={<CategorySearchPage />} />
        <Route 
          path="/admin-dashboard" 
          element={authState.isAuthenticated && authState.isAdmin ? <AdminDashboard /> : <Navigate to="/login" replace />} 
        />
      </Routes>
    </div>
  );
}

export default App;
