import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import api from '../services/api';
import Footer from '../components/Footer';
import { toast } from 'react-toastify';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const location = useLocation();
  const { product } = location.state || {};

  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);
  const [address, setAddress] = useState({
    fullName: '',
    line1: '',
    line2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    phone: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (product) {
          const res = await api.get(`/v1/products/get/${product.id}`);
          const normalizeProduct = (data) => ({
            id: data.id,
            name: data.name,
            price: data.price,
            imageUrl: data.imageUrl || data.image || 'https://via.placeholder.com/80',
          });
          const productData = normalizeProduct(res.data);

          setCart({
            items: [{
              productId: productData.id,
              productName: productData.name,
              quantity: 1,
              price: productData.price,
              imageUrl: productData.imageUrl,
            }],
            total: productData.price,
          });
        } else {
          const res = await api.get('/v1/carts');
          const updatedItems = await Promise.all(
            res.data.items.map(async (item) => {
              const productRes = await api.get(`/v1/products/get/${item.productId}`);
              return {
                ...item,
                productName: productRes.data.name,
                imageUrl: productRes.data.imageUrl || productRes.data.image || 'https://via.placeholder.com/80',
              };
            })
          );
          setCart({ ...res.data, items: updatedItems });
        }
      } catch (err) {
        toast.error('Error loading data.');
      }
    };
    fetchData();
  }, [product]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const cardElement = elements.getElement(CardElement);
      if (!stripe || !elements || !cardElement) {
        toast.error('Stripe not ready.');
        setLoading(false);
        return;
      }

      const amount = Math.round(cart.total * 100);
      const cartItems = cart.items.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
      }));

      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        amount,
        currency: 'usd',
        paymentMethodId: 'pm_card_visa',
      });

      const { clientSecret, paymentIntentId } = paymentIntentRes.data;

      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: cardElement },
      });

      if (error) {
        toast.error(`Payment failed: ${error.message}`);
        setLoading(false);
        return;
      }

      const shippingAddress = {
        fullName: address.fullName,
        street: address.line1,
        city: address.city,
        state: address.state,
        zipCode: address.postalCode,
        country: address.country,
        phone: address.phone,
      };

      const checkoutRes = await api.post('/payment/checkout', {
        paymentIntentId: paymentIntent.id,
        shippingAddress,
        items: cartItems,
      });

      toast.success(`Order placed! Order ID: ${checkoutRes.data.orderId}`);
      navigate(`/order/${checkoutRes.data.orderId}`);
    } catch (err) {
      console.error(err);
      toast.error(err.response?.data?.error || 'Checkout failed.');
    } finally {
      setLoading(false);
    }
  };

  if (!cart) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-cyan-50 flex items-center justify-center">
        <div className="bg-white/80 backdrop-blur-sm rounded-3xl shadow-2xl p-8 text-center border border-white/20">
          <div className="animate-spin rounded-full h-12 w-12 border-4 border-blue-600 border-t-transparent mx-auto mb-4"></div>
          <p className="text-lg text-gray-700 font-medium">Loading...</p>
        </div>
      </div>
    );
  }

  const itemsToDisplay = cart?.items || [];

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-blue-50 via-indigo-50 to-cyan-50">
      {/* Decorative background elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-10 w-32 h-32 bg-blue-200/30 rounded-full blur-3xl"></div>
        <div className="absolute top-40 right-20 w-48 h-48 bg-purple-200/20 rounded-full blur-3xl"></div>
        <div className="absolute bottom-20 left-1/4 w-40 h-40 bg-cyan-200/25 rounded-full blur-3xl"></div>
      </div>

      <div className="max-w-7xl w-full mx-auto flex-1 p-6 relative z-10">
        {/* Header */}
        <div className="text-center mb-10">
          <h2 className="text-4xl font-bold text-gray-800 mb-3">
            ğŸ›’ Secure Checkout
            {product ? (
              <span className="block text-2xl font-medium text-gray-600 mt-2">
                {product.name}
              </span>
            ) : ''}
          </h2>
          <div className="w-24 h-1 bg-gradient-to-r from-blue-500 to-purple-500 mx-auto rounded-full"></div>
        </div>

        <div className="grid lg:grid-cols-5 gap-8">
          {/* Order Summary */}
          <div className="lg:col-span-2">
            <div className="bg-white/70 backdrop-blur-sm rounded-3xl shadow-xl border border-white/30 overflow-hidden sticky top-6">
              <div className="bg-gradient-to-r from-blue-600 to-purple-600 p-6 text-white">
                <h3 className="text-2xl font-bold flex items-center">
                  ğŸ“‹ Order Summary
                </h3>
              </div>

              <div className="p-6">
                {itemsToDisplay.length > 0 ? (
                  <div className="space-y-4">
                    {itemsToDisplay.map(item => (
                      <div key={item.productId} className="flex items-center gap-4 p-4 bg-white/60 rounded-2xl border border-gray-100/50 hover:bg-white/80 transition-all duration-300 hover:shadow-lg">
                        <div className="relative">
                          <img
                            src={item.imageUrl}
                            alt={item.productName}
                            className="w-20 h-20 object-cover rounded-xl shadow-md"
                            onError={(e) => (e.target.src = 'https://via.placeholder.com/80')}
                          />
                          <div className="absolute -top-2 -right-2 bg-blue-600 text-white text-xs rounded-full w-6 h-6 flex items-center justify-center font-bold shadow-lg">
                            {item.quantity}
                          </div>
                        </div>
                        <div className="flex-1">
                          <h4 className="font-semibold text-gray-800 mb-1">{item.productName}</h4>
                          <p className="text-sm text-gray-500 mb-2">Qty: {item.quantity}</p>
                          <p className="font-bold text-blue-600 text-lg">â‚¹{item.price * item.quantity}</p>
                        </div>
                      </div>
                    ))}

                    <div className="border-t border-gray-200/50 pt-4 mt-6">
                      <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-2xl p-5 border border-blue-100">
                        <div className="flex justify-between items-center">
                          <span className="text-xl font-bold text-gray-800">ğŸ’° Total:</span>
                          <span className="text-2xl font-bold text-blue-600">â‚¹{cart?.total || 0}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <div className="text-6xl mb-4">ğŸ›ï¸</div>
                    <p className="text-gray-500 text-lg">No items in the cart.</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Shipping & Payment */}
          <div className="lg:col-span-3">
            <div className="bg-white/70 backdrop-blur-sm rounded-3xl shadow-xl border border-white/30 overflow-hidden">
              <form onSubmit={handleSubmit} className="space-y-8">
                {/* Shipping */}
                <div className="p-8 border-b border-gray-100/50">
                  <h3 className="text-2xl font-bold text-gray-800 mb-6 flex items-center">
                    ğŸšš Shipping Address
                  </h3>
                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
                    <div className="sm:col-span-2">
                      <Input 
                        label="Full Name" 
                        value={address.fullName} 
                        onChange={val => setAddress({ ...address, fullName: val })} 
                        required 
                        placeholder="Enter your full name"
                      />
                    </div>
                    <Input 
                      label="Address Line 1" 
                      value={address.line1} 
                      onChange={val => setAddress({ ...address, line1: val })} 
                      required 
                      placeholder="Street address"
                    />
                    <Input 
                      label="Address Line 2" 
                      value={address.line2} 
                      onChange={val => setAddress({ ...address, line2: val })} 
                      placeholder="Apartment, suite, etc."
                    />
                    <Input 
                      label="City" 
                      value={address.city} 
                      onChange={val => setAddress({ ...address, city: val })} 
                      required 
                      placeholder="City"
                    />
                    <Input 
                      label="State" 
                      value={address.state} 
                      onChange={val => setAddress({ ...address, state: val })} 
                      required 
                      placeholder="State"
                    />
                    <Input 
                      label="Postal Code" 
                      value={address.postalCode} 
                      onChange={val => setAddress({ ...address, postalCode: val })} 
                      required 
                      placeholder="ZIP/Postal code"
                    />
                    <Input 
                      label="Country" 
                      value={address.country} 
                      onChange={val => setAddress({ ...address, country: val })} 
                      required 
                      placeholder="Country"
                    />
                    <Input 
                      label="Phone" 
                      value={address.phone} 
                      onChange={val => setAddress({ ...address, phone: val })} 
                      required 
                      placeholder="Phone number"
                    />
                  </div>
                </div>

                {/* Payment */}
                <div className="p-8">
                  <div className="flex items-center justify-between mb-6">
                    <h3 className="text-2xl font-bold text-gray-800 flex items-center">
                      ğŸ’³ Payment Details
                    </h3>
                    <div className="flex space-x-2">
                      <div className="w-10 h-6 bg-blue-600 rounded text-white text-xs flex items-center justify-center font-bold">VISA</div>
                      <div className="w-10 h-6 bg-red-500 rounded text-white text-xs flex items-center justify-center font-bold">MC</div>
                      <div className="w-10 h-6 bg-green-600 rounded text-white text-xs flex items-center justify-center font-bold">AMEX</div>
                    </div>
                  </div>

                  <div className="bg-white/80 border-2 border-gray-200 rounded-2xl p-6 focus-within:border-blue-500 focus-within:bg-white transition-all duration-300 shadow-inner">
                    <CardElement
                      options={{
                        style: {
                          base: {
                            fontSize: '16px',
                            color: '#374151',
                            fontFamily: 'system-ui, -apple-system, sans-serif',
                            '::placeholder': {
                              color: '#9CA3AF',
                            },
                          },
                          invalid: {
                            color: '#EF4444',
                          },
                        },
                      }}
                    />
                  </div>

                  <div className="mt-6 p-4 bg-green-50 rounded-xl border border-green-200">
                    <div className="flex items-center text-sm text-green-800">
                      <span className="mr-2">ğŸ”’</span>
                      Your payment information is encrypted and secure
                    </div>
                  </div>
                </div>

                {/* Submit Button */}
                <div className="px-8 pb-8">
                  <button
                    type="submit"
                    disabled={!stripe || loading}
                    className={`w-full py-4 rounded-2xl text-white font-bold text-lg transition-all duration-300 transform ${
                      loading 
                        ? 'bg-gray-400 cursor-not-allowed' 
                        : 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 hover:scale-105 hover:shadow-2xl shadow-lg'
                    }`}
                  >
                    {loading ? (
                      <div className="flex items-center justify-center">
                        <div className="animate-spin rounded-full h-5 w-5 border-2 border-white border-t-transparent mr-3"></div>
                        Processing...
                      </div>
                    ) : (
                      <div className="flex items-center justify-center">
                        <span className="mr-2">ğŸ”</span>
                        Pay â‚¹{cart?.total || 0}
                      </div>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

const Input = ({ label, value, onChange, required = false, placeholder }) => (
  <div className="flex flex-col">
    <label className="text-sm font-semibold mb-2 text-gray-700">
      {label} 
      {required && <span className="text-red-500 ml-1">*</span>}
    </label>
    <input
      type="text"
      value={value}
      onChange={(e) => onChange(e.target.value)}
      required={required}
      placeholder={placeholder}
      className="border-2 border-gray-200 rounded-xl px-4 py-3 focus:outline-none focus:border-blue-500 focus:bg-white bg-white/80 transition-all duration-300 hover:border-gray-300 placeholder-gray-400"
    />
  </div>
);

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
