import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardElement,
  useStripe,
  useElements
} from '@stripe/react-stripe-js';
import api from '../services/api';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);
  const [address, setAddress] = useState({
    fullName: '',
    line1: '',
    line2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    phone: ''
  });

  const userId = JSON.parse(localStorage.getItem('user'))?.id;

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await api.get('/v1/carts');
        setCart(res.data);
      } catch (err) {
        alert('Error loading cart.');
        console.error(err);
      }
    };
    fetchCart();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      if (!token || !userId) {
        navigate('/login');
        return;
      }

      const items = cart.items.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
        price: parseFloat((item.price * 100).toFixed(2))
      }));

      const cardElement = elements.getElement(CardElement);

      // Step 1: Create payment method
      const { paymentMethod, error: pmError } = await stripe.createPaymentMethod({
        type: 'card',
        card: cardElement
      });

      if (pmError) {
        alert(`Payment method creation failed: ${pmError.message}`);
        return;
      }

      // Step 2: Create payment intent with backend
      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        currency: 'usd',
        amount: Math.round(cart.total * 100),
        userId,
        paymentMethodId: paymentMethod.id,
        items
      });

      const { clientSecret, paymentIntentId } = paymentIntentRes.data;

      // Step 3: Confirm payment
      const { error: confirmError, paymentIntent } = await stripe.confirmCardPayment(clientSecret);

      if (confirmError || paymentIntent.status !== 'succeeded') {
        alert(`Payment failed: ${confirmError?.message || 'Unknown error'}`);
        return;
      }

      // Step 4: Finalize order by calling checkout API
      const cartItems = cart.items.map(item => ({
        productId: item.productId,
        quantity: item.quantity
      }));

      const shippingAddress = {
        street: address.line1,
        city: address.city,
        state: address.state,
        zipCode: address.postalCode,
        country: address.country
      };

      const checkoutRes = await api.post('/payment/checkout', {
        userId,
        paymentIntentId,
        shippingAddress,
        cartItems
      });

      alert(`Order placed successfully! Order ID: ${checkoutRes.data.orderId}`);
      navigate('/');
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || 'Checkout failed.');
    } finally {
      setLoading(false);
    }
  };

  if (!cart) return <div>Loading cart...</div>;

  return (
    <div className="container" style={{ maxWidth: '600px', marginTop: '30px' }}>
      <h2>Checkout</h2>
      <form onSubmit={handleSubmit}>
        <h3>Shipping Address</h3>
        {['fullName', 'line1', 'line2', 'city', 'state', 'postalCode', 'country', 'phone'].map(field => (
          <div key={field} style={{ marginBottom: '12px' }}>
            <label style={{ fontWeight: 'bold' }}>
              {field.charAt(0).toUpperCase() + field.slice(1)}:
            </label>
            <input
              type="text"
              required={field !== 'line2'}
              value={address[field]}
              onChange={(e) => setAddress({ ...address, [field]: e.target.value })}
              style={{
                width: '100%',
                padding: '8px',
                border: '1px solid #ccc',
                borderRadius: '4px'
              }}
            />
          </div>
        ))}

        <h3>Payment Details</h3>
        <div style={{ padding: '12px', border: '1px solid #ccc', borderRadius: '4px' }}>
          <CardElement />
        </div>

        <button
          type="submit"
          disabled={!stripe || loading}
          style={{
            marginTop: '20px',
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: '#fff',
            border: 'none',
            borderRadius: '4px',
            cursor: loading ? 'not-allowed' : 'pointer'
          }}
        >
          {loading ? 'Processing...' : `Pay ₹${cart.total}`}
        </button>
      </form>
    </div>
  );
}

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
