import React, { useEffect, useState } from 'react';
import { Package, Calendar, Eye, ShoppingBag } from 'lucide-react';

// Mock API and navigation for demo
const mockApi = {
  get: () => Promise.resolve({
    data: [
      {
        id: 'ORD001',
        status: 'Delivered',
        total: 2499,
        createdAt: '2024-06-20T10:30:00Z'
      },
      {
        id: 'ORD002',
        status: 'Processing',
        total: 1299,
        createdAt: '2024-06-22T14:15:00Z'
      },
      {
        id: 'ORD003',
        status: 'Shipped',
        total: 3999,
        createdAt: '2024-06-23T09:45:00Z'
      }
    ]
  })
};

const mockNavigate = (path) => console.log(`Navigate to: ${path}`);

// Mock Footer component
const Footer = () => (
  <div className="bg-gray-800 text-white p-8 mt-12 rounded-lg">
    <div className="text-center">
      <p>&copy; 2024 Your Store. All rights reserved.</p>
    </div>
  </div>
);

function MyOrders() {
  const [orders, setOrders] = useState([]);
  const navigate = mockNavigate; // Replace with useNavigate() in your actual code

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await mockApi.get('/orders/user'); // Replace with api.get('/orders/user') in your actual code
        setOrders(res.data);
      } catch (err) {
        console.error('Failed to fetch orders', err);
        alert('Error loading your orders');
      }
    };

    fetchOrders();
  }, []);

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'delivered':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'shipped':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'processing':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'cancelled':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };


  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      <div className="max-w-6xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center gap-3 mb-2">
            <div className="p-2 bg-blue-600 rounded-lg">
              <ShoppingBag className="w-6 h-6 text-white" />
            </div>
            <h1 className="text-3xl font-bold text-gray-900">My Orders</h1>
          </div>
          <p className="text-gray-600">Track and manage your order history</p>
        </div>

        {orders.length === 0 ? (
          <div className="text-center py-16">
            <div className="mb-6">
              <div className="mx-auto w-24 h-24 bg-gray-100 rounded-full flex items-center justify-center">
                <Package className="w-12 h-12 text-gray-400" />
              </div>
            </div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">No orders yet</h3>
            <p className="text-gray-600 mb-6">You haven't placed any orders yet. Start shopping to see your orders here!</p>
            <button className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg font-medium transition-colors">
              Start Shopping
            </button>
          </div>
        ) : (
          <div className="space-y-6">
            {orders.map(order => (
              <div
                key={order.id}
                className="bg-white rounded-xl shadow-sm border border-gray-200 hover:shadow-md transition-all duration-200 overflow-hidden"
              >
                <div className="p-6">
                  {/* Order Header */}
                  <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-4">
                    <div className="flex items-center gap-3 mb-2 sm:mb-0">
                      <div className="p-2 bg-blue-50 rounded-lg">
                        <Package className="w-5 h-5 text-blue-600" />
                      </div>
                      <div>
                        <h3 className="text-lg font-semibold text-gray-900">
                          Order #{order.id}
                        </h3>
                        <div className="flex items-center gap-2 text-sm text-gray-500">
                          <Calendar className="w-4 h-4" />
                          <span>{new Date(order.createdAt).toLocaleString()}</span>
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center gap-3">
                      <span className={`inline-flex items-center gap-1 px-3 py-1 rounded-full text-sm font-medium border ${getStatusColor(order.status)}`}>
                        <span>{getStatusIcon(order.status)}</span>
                        {order.status}
                      </span>
                    </div>
                  </div>

                  {/* Order Details */}
                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-6">
                    <div className="bg-gray-50 rounded-lg p-4">
                      <div className="text-sm text-gray-600 mb-1">Total Amount</div>
                      <div className="text-2xl font-bold text-gray-900">₹{order.total.toLocaleString()}</div>
                    </div>

                    <div className="bg-gray-50 rounded-lg p-4">
                      <div className="text-sm text-gray-600 mb-1">Order Status</div>
                      <div className="text-lg font-semibold text-gray-900 capitalize">{order.status}</div>
                    </div>
                  </div>

                  {/* Action Button */}
                  <div className="flex justify-end pt-4 border-t border-gray-100">
                    <button
                      onClick={() => navigate(`/order/${order.id}`)}
                      className="inline-flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white px-6 py-2.5 rounded-lg font-medium transition-all duration-200 hover:scale-105 active:scale-95"
                    >
                      <Eye className="w-4 h-4" />
                      View Details
                    </button>
                  </div>
                </div>

                {/* Progress bar for visual appeal */}
                <div className="h-1 bg-gradient-to-r from-blue-500 to-purple-500"></div>
              </div>
            ))}
          </div>
        )}

        {/* Footer */}
        <Footer />
      </div>
    </div>
  );
}

export default MyOrders;
