import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardElement,
  useStripe,
  useElements
} from '@stripe/react-stripe-js';
import api from '../services/api';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);
  const [address, setAddress] = useState({
    fullName: '',
    line1: '',
    line2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    phone: ''
  });

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await api.get('/v1/carts');
        setCart(res.data);
      } catch (err) {
        alert('Error loading cart.');
        console.error(err);
      }
    };

    fetchCart();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (!stripe || !elements) {
        alert('Stripe is not ready yet.');
        return;
      }

      const cardElement = elements.getElement(CardElement);
      if (!cardElement) {
        alert('Card details are missing');
        return;
      }

      // Step 1: Create PaymentIntent on backend
      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        currency: 'usd',
        paymentMethodId: 'pm_card_visa' // Still required for Stripe API call
      });

      const { clientSecret, paymentIntentId } = paymentIntentRes.data;

      // Step 2: Confirm card payment with Stripe Elements
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
          billing_details: {
            name: address.fullName,
            phone: address.phone,
            address: {
              line1: address.line1,
              line2: address.line2,
              city: address.city,
              state: address.state,
              postal_code: address.postalCode,
              country: address.country
            }
          }
        }
      });

      if (error || paymentIntent.status !== 'succeeded') {
        alert(`Payment failed: ${error?.message || 'Something went wrong'}`);
        return;
      }

      // Step 3: Finalize checkout
      const shippingAddress = {
        fullName: address.fullName,
        line1: address.line1,
        line2: address.line2,
        city: address.city,
        state: address.state,
        postalCode: address.postalCode,
        country: address.country,
        phone: address.phone
      };

      const checkoutRes = await api.post('/payment/checkout', {
        userId: cart.userId,
        paymentIntentId: paymentIntentId,
        shippingAddress
      });

      alert(`Order placed successfully! Order ID: ${checkoutRes.data.orderId}`);
      navigate('/');
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || 'Checkout failed.');
    } finally {
      setLoading(false);
    }
  };

  if (!cart) return <div>Loading cart...</div>;

  return (
    <div className="container" style={{ maxWidth: '600px', marginTop: '30px' }}>
      <h2>Checkout</h2>
      <form onSubmit={handleSubmit}>
        <h3>Shipping Address</h3>
        {[
          { name: 'fullName', label: 'Full Name' },
          { name: 'line1', label: 'Address Line 1' },
          { name: 'line2', label: 'Address Line 2 (Optional)', required: false },
          { name: 'city', label: 'City' },
          { name: 'state', label: 'State' },
          { name: 'postalCode', label: 'Postal Code' },
          { name: 'country', label: 'Country' },
          { name: 'phone', label: 'Phone' }
        ].map(({ name, label, required = true }) => (
          <div key={name} style={{ marginBottom: '12px' }}>
            <label style={{ fontWeight: 'bold' }}>{label}:</label>
            <input
              type="text"
              required={required}
              value={address[name]}
              onChange={(e) => setAddress({ ...address, [name]: e.target.value })}
              style={{
                width: '100%',
                padding: '8px',
                border: '1px solid #ccc',
                borderRadius: '4px'
              }}
            />
          </div>
        ))}

        <h3>Payment Details</h3>
        <div style={{ padding: '12px', border: '1px solid #ccc', borderRadius: '4px' }}>
          <CardElement />
        </div>

        <button
          type="submit"
          disabled={!stripe || loading}
          style={{
            marginTop: '20px',
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: '#fff',
            border: 'none',
            borderRadius: '4px',
            cursor: loading ? 'not-allowed' : 'pointer'
          }}
        >
          {loading ? 'Processing...' : `Pay ₹${cart.total}`}
        </button>
      </form>
    </div>
  );
}

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
