import React, { useState, useEffect } from 'react';
import api from '../services/api';
import ProductCard from '../components/ProductCard';
import CategorySlider from '../components/CategorySlider';

function Home() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [wishlistIds, setWishlistIds] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    fetchCategories();
    fetchProducts();
    fetchWishlist();
  }, []);

  const fetchCategories = async () => {
    try {
      const res = await api.get('/categories/getCategory');
      setCategories(res.data);
    } catch (err) {
      console.error('Error fetching categories:', err);
    }
  };

  const fetchProducts = async (category = '') => {
    try {
      const res = category
        ? await api.get(`/v1/products/category/byname?name=${encodeURIComponent(category)}`)
        : await api.get('/v1/products/getproducts');
      setProducts(res.data);
    } catch (err) {
      console.error('Error fetching products:', err);
    }
  };

  const fetchWishlist = async () => {
    if (!token) return;
    try {
      const res = await api.get('/v1/wishlist/list');
      const ids = res.data.map((item) => item.id);
      setWishlistIds(ids);
    } catch (err) {
      console.error('Error fetching wishlist:', err);
    }
  };

  const handleCategoryClick = (categoryName) => {
    setSelectedCategory(categoryName);
    fetchProducts(categoryName);
  };

  return (
    <div className="container">
      <CategorySlider onBannerClick={handleCategoryClick} />

      <div style={{ margin: '20px 0' }}>
        <button
          className="button"
          onClick={() => {
            setSelectedCategory('');
            fetchProducts();
          }}
        >
          All
        </button>
        {categories.map((cat) => (
          <button
            key={cat.id}
            className="button"
            style={{ marginLeft: '10px' }}
            onClick={() => handleCategoryClick(cat.name)}
          >
            {cat.name}
          </button>
        ))}
      </div>

      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {products
          .filter((p) => p && p.id)
          .map((product) => (
            <ProductCard
              key={product.id}
              product={product}
              wishlistIds={wishlistIds}
              setWishlistIds={setWishlistIds}
            />
          ))}
      </div>
    </div>
  );
}

export default Home;
