import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import api from '../services/api';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const [cart, setCart] = useState(null);
  const [address, setAddress] = useState({ street: '', city: '', state: '', zip: '', country: '' });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      const res = await api.get('/v1/carts');
      setCart(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Create Payment Intent
    try {
      const items = cart.items.map((item) => ({
        productId: item.productId,
        quantity: item.quantity,
        price: item.price * 100
      }));

      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        userId: parseInt(localStorage.getItem('userId')),
        amount: cart.total * 100,
        currency: 'INR',
        items: items,
        paymentMethodId: 'pm_card_visa'
      });

      const clientSecret = paymentIntentRes.data.clientSecret;

      const cardElement = elements.getElement(CardElement);
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
        },
      });

      if (error) {
        console.error(error);
        alert('Payment failed');
        setLoading(false);
        return;
      }

      // Proceed to checkout
      const checkoutRes = await api.post('/payment/checkout', {
        userId: parseInt(localStorage.getItem('userId')),
        paymentIntentId: paymentIntent.id,
        shippingAddress: address,
        items: items
      });

      alert('Order placed successfully! Order ID: ' + checkoutRes.data.orderId);
      navigate('/');
    } catch (err) {
      console.error(err);
      alert('Checkout failed');
    }
    setLoading(false);
  };

  if (!cart) return <div>Loading...</div>;

  return (
    <div className="container" style={{ maxWidth: '600px', marginTop: '20px' }}>
      <h2>Checkout</h2>
      <form onSubmit={handleSubmit}>
        <h3>Shipping Address</h3>
        <div>
          <label>Street:</label>
          <input type="text" value={address.street} onChange={(e) => setAddress({ ...address, street: e.target.value })} required />
        </div>
        <div>
          <label>City:</label>
          <input type="text" value={address.city} onChange={(e) => setAddress({ ...address, city: e.target.value })} required />
        </div>
        <div>
          <label>State:</label>
          <input type="text" value={address.state} onChange={(e) => setAddress({ ...address, state: e.target.value })} required />
        </div>
        <div>
          <label>ZIP:</label>
          <input type="text" value={address.zip} onChange={(e) => setAddress({ ...address, zip: e.target.value })} required />
        </div>
        <div>
          <label>Country:</label>
          <input type="text" value={address.country} onChange={(e) => setAddress({ ...address, country: e.target.value })} required />
        </div>
        <h3>Payment Details</h3>
        <CardElement />
        <button className="button" type="submit" disabled={!stripe || loading} style={{ marginTop: '20px' }}>
          {loading ? 'Processing...' : 'Pay ₹' + cart.total}
        </button>
      </form>
    </div>
  );
}

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
