import React, { useEffect, useState } from 'react';
import { useSearchParams } from 'react-router-dom';
import api from '../services/api';
import ProductCard from '../components/ProductCard';

function CategorySearchPage() {
  const [searchParams] = useSearchParams();
  const categoryName = searchParams.get('name');
  const [products, setProducts] = useState([]);
  const [brands, setBrands] = useState([]);
  const [selectedBrand, setSelectedBrand] = useState('');

  useEffect(() => {
    if (categoryName) {
      api.get(`/v1/products/category/byname?name=${categoryName}`)
        .then(res => {
          setProducts(res.data);
          const brandSet = new Set(res.data.map(p => p.brand));
          setBrands(Array.from(brandSet));
        })
        .catch(err => console.error(err));
    }
  }, [categoryName]);

  const filteredProducts = selectedBrand
    ? products.filter(p => p.brand === selectedBrand)
    : products;

  return (
    <div style={{ display: 'flex', padding: '20px' }}>
      {/* Filters Sidebar */}
      <div style={{ width: '250px', marginRight: '20px' }}>
        <h3>FILTERS</h3>

        <div>
          <strong>Categories</strong>
          <p>{categoryName} ({products.length})</p>
        </div>

        <div>
          <strong>Brand</strong>
          {brands.slice(0, 10).map(brand => (
            <div key={brand}>
              <input
                type="radio"
                name="brand"
                value={brand}
                onChange={() => setSelectedBrand(brand)}
                checked={selectedBrand === brand}
              />
              <label style={{ marginLeft: '8px' }}>{brand}</label>
            </div>
          ))}
          {brands.length > 10 && <p>+ {brands.length - 10} more</p>}
        </div>

        <div>
          <strong>Price</strong>
          <p>₹100 - ₹10,000+</p>
        </div>

        <div>
          <strong>Color</strong>
          {/* Just display mock options for now */}
          <div><input type="checkbox" /> Black (37929)</div>
          <div><input type="checkbox" /> White (27007)</div>
          <div><input type="checkbox" /> Blue (24378)</div>
          <div><input type="checkbox" /> + More</div>
        </div>
      </div>

      {/* Products Grid */}
      <div style={{ flex: 1, display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: '20px' }}>
        {filteredProducts.map(product => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
}

export default CategorySearchPage;
