import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardElement,
  useStripe,
  useElements
} from '@stripe/react-stripe-js';
import api from '../services/api';
import Footer from '../components/Footer';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);
  const [address, setAddress] = useState({
    fullName: '',
    line1: '',
    line2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    phone: ''
  });

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await api.get('/v1/carts');
        setCart(res.data);
      } catch (err) {
        alert('Error loading cart.');
        console.error(err);
      }
    };

    fetchCart();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const cardElement = elements.getElement(CardElement);
      if (!stripe || !elements || !cardElement) {
        alert('Stripe not ready');
        return;
      }

      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        amount: Math.round(cart.total * 100),
        currency: 'usd',
        paymentMethodId: 'pm_card_visa'
      });

      const { clientSecret, paymentIntentId } = paymentIntentRes.data;

      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: cardElement }
      });

      if (error) {
        alert(`Payment failed: ${error.message}`);
        return;
      }

      const shippingAddress = {
        fullName: address.fullName,
        street: address.line1,
        city: address.city,
        state: address.state,
        zipCode: address.postalCode,
        country: address.country,
        phone: address.phone
      };

      const checkoutRes = await api.post('/payment/checkout', {
        paymentIntentId: paymentIntent.id,
        shippingAddress
      });

      alert(`Order placed successfully! Order ID: ${checkoutRes.data.orderId}`);
      navigate(`/order/${checkoutRes.data.orderId}`);
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || 'Checkout failed.');
    } finally {
      setLoading(false);
    }
  };

  if (!cart) return <div>Loading cart...</div>;

  return (
    <div
      style={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        backgroundColor: '#f9f9f9',
      }}
    >
      {/* Content Wrapper */}
      <div style={{ width: '100%', maxWidth: '1200px', flex: 1, padding: '30px 20px' }}>
        <h2 style={{ fontSize: '28px', fontWeight: '600', color: '#333', marginBottom: '20px' }}>Checkout</h2>
        <form onSubmit={handleSubmit}>
          <h3 style={{ fontSize: '20px', fontWeight: '600', color: '#333', marginBottom: '15px' }}>Shipping Address</h3>

          <div style={{ marginBottom: '12px', display: 'flex', gap: '12px' }}>
            <div style={{ flex: 1 }}>
              <label style={{ fontWeight: 'bold' }}>Line 1:</label>
              <input type="text" required value={address.line1} onChange={(e) => setAddress({ ...address, line1: e.target.value })} style={inputStyle} />
            </div>
            <div style={{ flex: 1 }}>
              <label style={{ fontWeight: 'bold' }}>Line 2:</label>
              <input type="text" value={address.line2} onChange={(e) => setAddress({ ...address, line2: e.target.value })} style={inputStyle} />
            </div>
          </div>

          <div style={{ marginBottom: '12px', display: 'flex', gap: '12px' }}>
            <div style={{ flex: 1 }}>
              <label style={{ fontWeight: 'bold' }}>City:</label>
              <input type="text" required value={address.city} onChange={(e) => setAddress({ ...address, city: e.target.value })} style={inputStyle} />
            </div>
            <div style={{ flex: 1 }}>
              <label style={{ fontWeight: 'bold' }}>State:</label>
              <input type="text" required value={address.state} onChange={(e) => setAddress({ ...address, state: e.target.value })} style={inputStyle} />
            </div>
            <div style={{ flex: 1 }}>
              <label style={{ fontWeight: 'bold' }}>Postal Code:</label>
              <input type="text" required value={address.postalCode} onChange={(e) => setAddress({ ...address, postalCode: e.target.value })} style={inputStyle} />
            </div>
          </div>

          <div style={{ marginBottom: '12px' }}>
            <label style={{ fontWeight: 'bold' }}>Country:</label>
            <input type="text" required value={address.country} onChange={(e) => setAddress({ ...address, country: e.target.value })} style={inputStyle} />
          </div>

          <div style={{ marginBottom: '12px' }}>
            <label style={{ fontWeight: 'bold' }}>Phone:</label>
            <input type="tel" required value={address.phone} onChange={(e) => setAddress({ ...address, phone: e.target.value })} style={inputStyle} />
          </div>

          <h3 style={{ fontSize: '20px', fontWeight: '600', color: '#333', marginBottom: '15px' }}>Payment Details</h3>
          <div style={{ padding: '12px', border: '1px solid #ccc', borderRadius: '4px', marginBottom: '20px' }}>
            <CardElement />
          </div>

          <button
            type="submit"
            disabled={!stripe || loading}
            style={{
              marginTop: '20px',
              padding: '12px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '4px',
              cursor: loading ? 'not-allowed' : 'pointer',
              width: '100%',
              fontSize: '16px',
            }}
          >
            {loading ? 'Processing...' : `Pay ₹${cart.total}`}
          </button>
        </form>
      </div>

      {/* Footer with max width */}
      <div style={{ width: '100%', maxWidth: '1200px', marginTop: 'auto' }}>
        <Footer />
      </div>
    </div>
  );
}

// Shared input styling
const inputStyle = {
  width: '100%',
  padding: '8px',
  border: '1px solid #ccc',
  borderRadius: '4px',
};

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
