import React from 'react';
import { Link } from 'react-router-dom';

function ProductCard({ product }) {
  return (
    <div style={{ border: '1px solid #e0e0e0', borderRadius: '4px', background: 'white', padding: '10px', margin: '10px', width: '200px' }}>
      <Link to={`/product/${product.id}`}>
        <img src={product.imageUrl} alt={product.name} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
        <h3 style={{ fontSize: '16px', margin: '10px 0' }}>{product.name}</h3>
        <p style={{ color: '#666' }}>â‚¹{product.price}</p>
      </Link>
    </div>
  );
}

export default ProductCard;
