import React from 'react';
import { Link } from 'react-router-dom';

function ProductCard({ product }) {
  if (!product || typeof product !== 'object') {
    console.warn('Invalid product passed to ProductCard:', product);
    return null; // Don't render anything if product is invalid
  }

  return (
    <div className="card" style={{ width: '200px', margin: '10px' }}>
      <img
        src={product.image || 'https://via.placeholder.com/200'}
        alt={product.name || 'Product'}
        style={{ width: '100%', height: '200px' }}
      />
      <div className="card-body">
        <h4>{product.name || 'No Name'}</h4>
        <p>â‚¹{product.price || 'N/A'}</p>
        <Link to={`/product/${product.id}`} className="button">View</Link>
      </div>
    </div>
  );
}

export default ProductCard;
