import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function Cart() {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const fetchCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const res = await api.get('/api/v1/carts/items');
      setCart(res.data);
    } catch (err) {
      if (err.response?.status === 401) {
        setError('Session expired or unauthorized. Please login again.');
      } else {
        console.error(err);
        setError('Failed to load cart. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const handleRemove = async (itemId) => {
    const token = localStorage.getItem('token');
    try {
      await api.delete(`/api/v1/carts/items/${itemId}`);
      await fetchCart();
    } catch (err) {
      console.error(err);
      setError('Failed to remove item. Please try again.');
    }
  };

  const handleClear = async () => {
    const token = localStorage.getItem('token');
    try {
      await api.delete('/api/v1/carts');
      setCart({ items: [], total: 0 });
    } catch (err) {
      console.error(err);
      setError('Failed to clear cart. Please try again.');
    }
  };

  const handleUpdateQuantity = async (itemId, quantity) => {
    if (quantity < 1) return;
    const token = localStorage.getItem('token');
    try {
      await api.put(`/api/v1/carts/items/${itemId}`, { quantity });
      await fetchCart();
    } catch (err) {
      console.error(err);
      setError('Failed to update quantity. Please try again.');
    }
  };

  const handleRelogin = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (loading) return <div>Loading cart...</div>;

  if (error) {
    return (
      <div style={{ padding: '20px', color: 'red' }}>
        <p>{error}</p>
        {error.includes('Session expired') && (
          <button className="button" onClick={handleRelogin}>
            Login Again
          </button>
        )}
      </div>
    );
  }

  if (!cart) return <div>No cart data.</div>;

  return (
    <div className="container">
      <h2>Your Cart</h2>
      {cart.items.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          {cart.items.map((item) => (
            <div
              key={item.itemId}
              style={{
                display: 'flex',
                alignItems: 'center',
                marginBottom: '10px',
                background: 'white',
                padding: '10px',
                borderRadius: '4px',
              }}
            >
              <img
                src={`/api/v1/products/image/${item.product.imageUrl}`}
                alt={item.product.name}
                style={{ width: '100px', height: '100px', objectFit: 'cover' }}
              />
              <div style={{ marginLeft: '20px', flex: 1 }}>
                <h4>{item.product.name}</h4>
                <p>Price: ₹{item.product.price}</p>
                <p>Subtotal: ₹{item.subtotal}</p>
                <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                  <button
                    className="button"
                    onClick={() => handleUpdateQuantity(item.itemId, item.quantity - 1)}
                    disabled={item.quantity <= 1}
                  >
                    -
                  </button>
                  <span>Qty: {item.quantity}</span>
                  <button
                    className="button"
                    onClick={() => handleUpdateQuantity(item.itemId, item.quantity + 1)}
                  >
                    +
                  </button>
                  <button
                    className="button"
                    onClick={() => handleRemove(item.itemId)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            </div>
          ))}
          <h3>Total: ₹{cart.total}</h3>
          <button className="button" onClick={() => navigate('/checkout')} style={{ marginTop: '10px' }}>
            Proceed to Checkout
          </button>
          <button
            className="button"
            onClick={handleClear}
            style={{ marginTop: '10px', marginLeft: '10px' }}
          >
            Clear Cart
          </button>
        </div>
      )}
    </div>
  );
}

export default Cart;
