import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';
import ProductCard from '../components/ProductCard';
import Footer from '../components/Footer';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [similarProducts, setSimilarProducts] = useState([]);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/get/${id}`);
      setProduct(res.data);
      // Extract category name, assuming it might be an object
      const categoryName = typeof res.data.category === 'object' ? res.data.category.name : res.data.category;
      if (categoryName) {
        fetchSimilarProducts(categoryName);
      } else {
        console.warn('Category name not found in product data:', res.data);
      }
    } catch (err) {
      console.error('Error fetching product:', err);
      setError('❌ Product not found');
    }
  };

  const fetchSimilarProducts = async (categoryName) => {
    try {
      const res = await api.get(`/v1/products/category/byname?name=${encodeURIComponent(categoryName)}`);
      console.log('Similar products response:', res.data); // Debug log
      // Filter out the current product from similar products
      const filteredProducts = res.data.length > 1 ? res.data.filter(p => p.id !== Number(id)) : res.data;
      setSimilarProducts(filteredProducts);
      console.log('Filtered similar products:', filteredProducts); // Debug log
    } catch (err) {
      console.error('Error fetching similar products:', err);
    }
  };

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login'); 
      return;
    }

    try {
      await api.post('/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      });
      setMessage('✔️ Product added to cart!');
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      console.error('Add to cart error:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setMessage('❌ Failed to add to cart.');
      }
    }
  };

  if (error) {
    return <div style={{ padding: '20px', color: 'red' }}>{error}</div>;
  }

  if (!product) {
    return <div style={{ padding: '20px' }}>Loading...</div>;
  }

  return (
    <div style={{ margin: '20px 0', textAlign: 'center' }}>
      <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
        <img
          src={product.imageUrl || '/placeholder.png'}
          onError={(e) => { e.target.src = '/placeholder.png'; }}
          alt={product.name}
          style={{
            width: '200px',
            height: '200px',
            objectFit: 'cover',
            borderRadius: '8px',
          }}
        />
        <div>
          <h2>{product.name}</h2>
          <p style={{ fontSize: '18px', fontWeight: 'bold' }}>₹{product.price}</p>
          <p>{product.description || 'No description available'}</p>
          <p><strong>Brand:</strong> {product.brand}</p>
          <p><strong>Category:</strong> {product.category}</p>
          <button
            style={{
              backgroundColor: '#ff4081',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '5px',
              cursor: 'pointer',
              marginRight: '10px',
            }}
            onClick={handleAddToCart}
          >
            Add to Cart
          </button>
          <button
            style={{
              backgroundColor: '#ff4081',
              color: 'white',
              border: 'none',
              padding: '10px 20px',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
            onClick={() => navigate('/')}
          >
            ⬅ Back to Home
          </button>
          {message && (
            <p style={{ marginTop: '10px', color: message.includes('✔️') ? 'green' : 'red' }}>
              {message}
            </p>
          )}
        </div>
      </div>

      <div style={{ marginTop: '40px' }}>
        <h3 style={{ fontSize: '20px', marginBottom: '20px' }}>Similar Products</h3>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
          {similarProducts.length > 0 ? (
            similarProducts.map(product => (
              <ProductCard key={product.id} product={product} />
            ))
          ) : (
            <p>No similar products found.</p>
          )}
        </div>
      </div>
      {/* Footer */}
      <br></br>
      <br></br>
      <Footer />
    </div>
  );
}

export default ProductDetail;
