import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function Cart() {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const fetchCart = async () => {
    setLoading(true);
    setError('');

    try {
      const res = await api.get('/v1/carts');
      const updatedItems = await Promise.all(
        res.data.items.map(async (item) => {
          try {
            const productRes = await api.get(`/v1/products/get/${item.productId}`);
            console.log('Fetched product for itemId', item.itemId, ':', productRes.data);
            return { ...item, product: productRes.data };
          } catch (productErr) {
            console.error('Error fetching product for itemId', item.itemId, ':', productErr);
            return item;
          }
        })
      );
      setCart({ ...res.data, items: updatedItems });
    } catch (err) {
      if (err.response?.status === 401) {
        console.warn('Unauthorized - token might be invalid');
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        console.error(err);
        setError('Failed to load cart. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const handleRemove = async (itemId) => {
    try {
      await api.delete(`/v1/carts/items/${itemId}`);
      await fetchCart();
    } catch (err) {
      console.error(err);
      setError('Failed to remove item. Please try again.');
    }
  };

  const handleClear = async () => {
    try {
      await api.delete('/v1/carts');
      setCart({ items: [], total: 0 });
    } catch (err) {
      console.error(err);
      setError('Failed to clear cart. Please try again.');
    }
  };

  const handleUpdateQuantity = async (itemId, quantity) => {
    if (quantity < 1) return;
    try {
      await api.put(`/v1/carts/items/${itemId}`, { quantity });
      await fetchCart();
    } catch (err) {
      console.error(err);
      setError('Failed to update quantity. Please try again.');
    }
  };

  if (loading) return <div>Loading cart...</div>;

  if (error) {
    return (
      <div style={{ padding: '20px', color: 'red' }}>
        <p>{error}</p>
      </div>
    );
  }

  if (!cart || !cart.items) return <div>No cart data.</div>;

  return (
    <div className="container">
      <h2>Your Cart</h2>
      {cart.items.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          {cart.items.map((item) => (
            <div
              key={item.itemId}
              style={{
                display: 'flex',
                alignItems: 'center',
                marginBottom: '10px',
                background: 'white',
                padding: '10px',
                borderRadius: '4px',
              }}
            >
              <img
                src={
                  item.product?.imageUrl
                    ? item.product.imageUrl // âœ… Full URL already returned from backend
                    : '/placeholder.png'
                }
                alt={item.product?.name || 'Product'}
                style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                onError={(e) => {
                  console.error('Image load failed for itemId', item.itemId);
                  e.target.src = '/placeholder.png';
                }}
              />
              <div style={{ marginLeft: '20px', flex: 1 }}>
                <h4>{item.product?.name || 'Unknown Product'}</h4>
                <p>Price: â‚¹{item.price || item.product?.price || 'N/A'}</p>
                <p>
                  Subtotal: â‚¹
                  {item.price && item.quantity
                    ? (item.price * item.quantity).toFixed(2)
                    : item.product?.price && item.quantity
                    ? (item.product.price * item.quantity).toFixed(2)
                    : 'N/A'}
                </p>
                <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                  <button
                    className="button"
                    onClick={() => handleUpdateQuantity(item.itemId, item.quantity - 1)}
                    disabled={item.quantity <= 1}
                  >
                    -
                  </button>
                  <span>Qty: {item.quantity}</span>
                  <button
                    className="button"
                    onClick={() => handleUpdateQuantity(item.itemId, item.quantity + 1)}
                  >
                    +
                  </button>
                  <button className="button" onClick={() => handleRemove(item.itemId)}>
                    Remove
                  </button>
                </div>
              </div>
            </div>
          ))}
          <h3>Total: â‚¹{cart.total || 0}</h3>
          <button
            className="button"
            onClick={() => navigate('/checkout')}
            style={{ marginTop: '10px' }}
          >
            Proceed to Checkout
          </button>
          <button
            className="button"
            onClick={handleClear}
            style={{ marginTop: '10px', marginLeft: '10px' }}
          >
            Clear Cart
          </button>
        </div>
      )}
    </div>
  );
}

export default Cart;
