import React, { useState, useEffect } from 'react';
import api from '../services/api';
import ProductCard from '../components/ProductCard';

function Home() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    fetchCategories();
    fetchProducts();
  }, []);

  const fetchCategories = async () => {
    try {
      const res = await api.get('/categories/getCategory');
      setCategories(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchProducts = async (category) => {
    try {
      let res;
      if (category) {
        res = await api.get(`/v1/products/category/byname?name=${category}`);
      } else {
        res = await api.get('/v1/products/getproducts');
      }
      setProducts(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    fetchProducts(category);
  };

  return (
    <div className="container">
      <div style={{ margin: '20px 0' }}>
        <button className="button" onClick={() => { setSelectedCategory(''); fetchProducts(); }}>All</button>
        {categories.map((cat) => (
          <button key={cat.id} className="button" style={{ marginLeft: '10px' }} onClick={() => handleCategoryClick(cat.name)}>
            {cat.name}
          </button>
        ))}
      </div>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {products
  .filter(product => product && product.id) // Ignore invalid entries
  .map((product) => (
    <ProductCard key={product.id} product={product} />
))}

      </div>
    </div>
  );
}

export default Home;
