import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardElement,
  useStripe,
  useElements,
} from '@stripe/react-stripe-js';
import api from '../services/api';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [address, setAddress] = useState({
    fullName: '',
    street: '',
    street2: '',
    city: '',
    state: '',
    zipCode: '',
    country: '',
    phone: '',
  });

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await api.get('/v1/carts');
        setCart(res.data);
      } catch (err) {
        setError('Failed to load cart. Please try again.');
        console.error(err);
      }
    };

    fetchCart();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      if (!stripe || !elements) {
        setError('Stripe has not loaded. Please try again.');
        return;
      }

      // Create payment method using CardElement
      const cardElement = elements.getElement(CardElement);
      const { error: paymentMethodError, payment_method } = await stripe.createPaymentMethod({
        type: 'card',
        card: cardElement,
        billing_details: {
          name: address.fullName,
          address: {
            line1: address.street,
            line2: address.street2 || null,
            city: address.city,
            state: address.state,
            postal_code: address.zipCode,
            country: address.country,
          },
          phone: address.phone || null,
        },
      });

      if (paymentMethodError) {
        setError(`Payment method error: ${paymentMethodError.message}`);
        setLoading(false);
        return;
      }

      // Prepare items for payment intent
      const items = cart.items.map((item) => ({
        productId: item.productId,
        quantity: item.quantity,
        price: Math.round(item.price * 100), // Convert to cents
      }));

      // Create payment intent with the payment method ID
      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        userId: cart.userId,
        amount: Math.round(cart.total * 100), // Convert total to cents
        currency: 'usd',
        paymentMethodId: payment_method.id, // Use dynamic payment method ID
        items,
      });

      const { clientSecret, paymentIntentId } = paymentIntentRes.data;

      // Confirm card payment
      const { error: confirmError, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: payment_method.id, // Reuse payment method
      });

      if (confirmError) {
        setError(`Payment failed: ${confirmError.message}`);
        setLoading(false);
        return;
      }

      // Prepare checkout request
      const cartItems = cart.items.map((item) => ({
        productId: item.productId,
        quantity: item.quantity,
      }));

      const shippingAddress = {
        street: address.street,
        city: address.city,
        state: address.state,
        zipCode: address.zipCode,
        country: address.country,
      };

      // Complete checkout
      const checkoutRes = await api.post('/payment/checkout', {
        userId: cart.userId,
        paymentIntentId: paymentIntent.id,
        shippingAddress,
        cartItems,
      });

      alert(`Order placed successfully! Order ID: ${checkoutRes.data.orderId}`);
      navigate('/');
    } catch (err) {
      console.error('Checkout error:', err);
      const errorMessage = err.response?.data?.error || err.message || 'Checkout failed. Please try again.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // Rest of the JSX remains the same as in the previous response
  if (!cart) {
    return (
      <div className="container" style={{ maxWidth: '600px', marginTop: '30px' }}>
        {error ? <div style={{ color: 'red' }}>{error}</div> : 'Loading cart...'}
      </div>
    );
  }

  return (
    <div className="container" style={{ maxWidth: '600px', marginTop: '30px' }}>
      <h2>Checkout</h2>
      {error && <div style={{ color: 'red', marginBottom: '20px' }}>{error}</div>}
      <form onSubmit={handleSubmit}>
        <h3>Shipping Address</h3>
        {[
          { key: 'fullName', label: 'Full Name', required: true },
          { key: 'street', label: 'Street Address', required: true },
          { key: 'street2', label: 'Apt/Suite (Optional)', required: false },
          { key: 'city', label: 'City', required: true },
          { key: 'state', label: 'State', required: true },
          { key: 'zipCode', label: 'Zip Code', required: true },
          { key: 'country', label: 'Country', required: true },
          { key: 'phone', label: 'Phone (Optional)', required: false },
        ].map(({ key, label, required }) => (
          <div key={key} style={{ marginBottom: '12px' }}>
            <label style={{ fontWeight: 'bold' }}>{label}:</label>
            <input
              type="text"
              required={required}
              value={address[key]}
              onChange={(e) => setAddress({ ...address, [key]: e.target.value })}
              style={{
                width: '100%',
                padding: '8px',
                border: '1px solid #ccc',
                borderRadius: '4px',
              }}
            />
          </div>
        ))}

        <h3>Payment Details</h3>
        <div style={{ padding: '12px', border: '1px solid #ccc', borderRadius: '4px' }}>
          <CardElement
            options={{
              style: {
                base: {
                  fontSize: '16px',
                  color: '#424770',
                  '::placeholder': { color: '#aab7c4' },
                },
                invalid: { color: '#9e2146' },
              },
            }}
          />
        </div>

        <button
          type="submit"
          disabled={!stripe || !elements || loading}
          style={{
            marginTop: '20px',
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: '#fff',
            border: 'none',
            borderRadius: '4px',
            cursor: loading || !stripe || !elements ? 'not-allowed' : 'pointer',
            opacity: loading || !stripe || !elements ? 0.6 : 1,
          }}
        >
          {loading ? 'Processing...' : `Pay $${(cart.total || 0).toFixed(2)}`}
        </button>
      </form>
    </div>
  );
}

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
