import React, { useEffect, useState } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import api from '../services/api';
import ProductCard from '../components/ProductCard';
import Footer from '../components/Footer';
import { FaSearch } from 'react-icons/fa';

function CategorySearchPage() {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const categoryName = searchParams.get('name');
  const [products, setProducts] = useState([]);
  const [brands, setBrands] = useState([]);
  const [selectedBrand, setSelectedBrand] = useState('');
  const [searchTerm, setSearchTerm] = useState(categoryName || '');
  const [showSuggestions, setShowSuggestions] = useState(false);

  const defaultCategories = ["Jeans", "T-shirts", "Shoes", "Jackets", "Sunglasses", "Caps", "Shirts"];

  useEffect(() => {
    if (categoryName) {
      api.get(`/v1/products/category/byname?name=${categoryName}`)
        .then(res => {
          setProducts(res.data);
          const brandSet = new Set(res.data.map(p => p.brand));
          setBrands(Array.from(brandSet));
        })
        .catch(err => console.error(err));
    }
  }, [categoryName]);

  const filteredProducts = selectedBrand
    ? products.filter(p => p.brand === selectedBrand)
    : products;

  const handleSearch = () => {
    if (searchTerm.trim()) {
      navigate(`/category/byname?name=${encodeURIComponent(searchTerm.trim())}`);
      setShowSuggestions(false);
    }
  };

  const handleSuggestionClick = (cat) => {
    setSearchTerm(cat);
    navigate(`/category/byname?name=${encodeURIComponent(cat)}`);
    setShowSuggestions(false);
  };

  return (
    <div style={{ margin: '40px 0', padding: '20px' }}>
      {/* Search Bar */}
      <div style={{ maxWidth: '400px', margin: '0 auto 30px', position: 'relative' }}>
        <input
          type="text"
          placeholder="Search categories..."
          value={searchTerm}
          onChange={(e) => {
            setSearchTerm(e.target.value);
            setShowSuggestions(true);
          }}
          onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
          onFocus={() => setShowSuggestions(true)}
          onBlur={() => setTimeout(() => setShowSuggestions(false), 200)}
          style={{
            width: '100%',
            padding: '10px 36px 10px 12px',
            fontSize: '15px',
            border: '1px solid #ccc',
            borderRadius: '6px'
          }}
        />
        <FaSearch
          onClick={handleSearch}
          style={{
            position: 'absolute',
            right: '10px',
            top: '50%',
            transform: 'translateY(-50%)',
            color: '#666',
            cursor: 'pointer'
          }}
        />
        {showSuggestions && (
          <ul style={{
            position: 'absolute',
            top: '100%',
            left: 0,
            right: 0,
            background: '#fff',
            border: '1px solid #ccc',
            borderRadius: '6px',
            maxHeight: '200px',
            overflowY: 'auto',
            listStyle: 'none',
            margin: 0,
            padding: 0,
            zIndex: 10
          }}>
            {defaultCategories
              .filter(cat => cat.toLowerCase().includes(searchTerm.toLowerCase()))
              .map(cat => (
                <li key={cat}>
                  <button
                    onClick={() => handleSuggestionClick(cat)}
                    style={{
                      width: '100%',
                      padding: '10px 12px',
                      textAlign: 'left',
                      background: 'none',
                      border: 'none',
                      cursor: 'pointer'
                    }}
                  >
                    <FaSearch style={{ marginRight: '8px', color: '#888' }} />
                    {cat}
                  </button>
                </li>
              ))}
          </ul>
        )}
      </div>

      {/* Main Content */}
      <div style={{ display: 'flex' }}>
        {/* Sidebar Filters */}
        <div style={{ width: '250px', marginRight: '20px' }}>
          <h3>FILTERS</h3>

          <div>
            <strong>Categories</strong>
            <p>{categoryName} ({products.length})</p>
          </div>

          <div>
            <strong>Brand</strong>
            {brands.slice(0, 10).map(brand => (
              <div key={brand}>
                <input
                  type="radio"
                  name="brand"
                  value={brand}
                  onChange={() => setSelectedBrand(brand)}
                  checked={selectedBrand === brand}
                />
                <label style={{ marginLeft: '8px' }}>{brand}</label>
              </div>
            ))}
            {brands.length > 10 && <p>+ {brands.length - 10} more</p>}
          </div>

          <div>
            <strong>Price</strong>
            <p>₹100 - ₹10,000+</p>
          </div>

          <div>
            <strong>Color</strong>
            <div><input type="checkbox" /> Black</div>
            <div><input type="checkbox" /> White</div>
            <div><input type="checkbox" /> Blue</div>
            <div><input type="checkbox" /> + More</div>
          </div>
        </div>

        {/* Product List */}
        <div style={{ flex: 1 }}>
          {filteredProducts.length === 0 ? (
            <p>No products found in "{categoryName}"</p>
          ) : (
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
              gap: '20px'
            }}>
              {filteredProducts.map(product => (
                <ProductCard key={product.id} product={product} />
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Footer */}
      <br /><br />
      <Footer />
    </div>
  );
}

export default CategorySearchPage;
