import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import api from '../services/api';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const [cart, setCart] = useState(null);
  const [address, setAddress] = useState({ street: '', city: '', state: '', zip: '', country: '' });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      const res = await api.get('/v1/carts');
      setCart(res.data);
    } catch (err) {
      console.error('Fetch cart error:', err.response?.data || err.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const userId = localStorage.getItem('userId');
      if (!userId) {
        alert('Please log in to proceed with checkout.');
        navigate('/login');
        return;
      }

      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        userId: parseInt(userId),
        currency: 'INR',
        paymentMethodId: 'pm_card_visa'
      });

      const { clientSecret } = paymentIntentRes.data;

      const cardElement = elements.getElement(CardElement);
      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
        },
      });

      if (error) {
        console.error('Payment error:', error);
        alert(`Payment failed: ${error.message}`);
        setLoading(false);
        return;
      }

      const items = cart.items.map((item) => ({
        productId: item.productId,
        quantity: item.quantity,
        price: item.price * 100
      }));

      const checkoutRes = await api.post('/payment/checkout', {
        userId: parseInt(userId),
        paymentIntentId: paymentIntent.id,
        shippingAddress: address,
        cartItems: items
      });

      alert(`Order placed successfully! Order ID: ${checkoutRes.data.orderId}`);
      navigate('/');
    } catch (err) {
      console.error('Checkout error:', err.response?.data || err.message);
      alert(`Checkout failed: ${err.response?.data?.error || err.message}`);
      setLoading(false);
    }
  };

  if (!cart) return <div>Loading...</div>;

  return (
    <div className="container" style={{ maxWidth: '600px', marginTop: '20px' }}>
      <h2>Checkout</h2>
      <form onSubmit={handleSubmit}>
        <h3>Shipping Address</h3>
        {['street', 'city', 'state', 'zip', 'country'].map((field) => (
          <div key={field}>
            <label>{field.charAt(0).toUpperCase() + field.slice(1)}:</label>
            <input
              type="text"
              value={address[field]}
              onChange={(e) => setAddress({ ...address, [field]: e.target.value })}
              required
            />
          </div>
        ))}
        <h3>Payment Details征求意见稿Details</h3>
        <CardElement />
        <button
          className="button"
          type="submit"
          disabled={!stripe || loading}
          style={{ marginTop: '20px' }}
        >
          {loading ? 'Processing...' : `Pay ₹${cart.total}`}
        </button>
      </form>
    </div>
  );
}

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
