import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    fetchProduct();
  }, []);

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/${id}`);
      setProduct(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      // Not logged in → redirect
      navigate('/login');
      return;
    }

    try {
      // Add item using product ID and default quantity = 1
      await api.post('/v1/carts/items', { productId: product.id, quantity: 1 });
      navigate('/cart');
    } catch (err) {
      if (err.response && err.response.status === 401) {
        // Token expired/invalid → redirect to login
        navigate('/login');
      } else {
        console.error('Add to cart failed:', err.response?.data || err);
        alert('Could not add item to cart.');
      }
    }
  };

  if (!product) return <div>Loading...</div>;

  return (
    <div className="container">
      <h2>{product.name}</h2>
      <p>Price: ₹{product.price}</p>
      <button className="button" onClick={handleAddToCart}>
        Add to Cart
      </button>
    </div>
  );
}

export default ProductDetail;
