import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function OrderDetails() {
  const { orderId } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const res = await api.get(`/orders/order/${orderId}`);
        setOrder(res.data);
      } catch (err) {
        console.error(err);
        alert('Failed to load order');
      }
    };

    fetchOrder();
  }, [orderId]);

  const cancelOrder = async () => {
    if (window.confirm('Are you sure you want to cancel this order?')) {
      try {
        await api.delete(`/orders/order/${orderId}`);
        alert('Order cancelled');
        navigate('/orders'); // Redirect to all orders
      } catch (err) {
        console.error(err);
        alert('Failed to cancel order');
      }
    }
  };

  if (!order) return <div>Loading order...</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Order #{order.id}</h2>
      <p><strong>Status:</strong> {order.status}</p>
      <p><strong>Total:</strong> ₹{order.total}</p>
      <p><strong>Created At:</strong> {new Date(order.createdAt).toLocaleString()}</p>

      <h3>Shipping Address</h3>
      <p>
        {order.shippingAddress.fullName},<br />
        {order.shippingAddress.street || order.shippingAddress.addressLine1},<br />
        {order.shippingAddress.city}, {order.shippingAddress.state} - {order.shippingAddress.zipCode}<br />
        {order.shippingAddress.country}<br />
        Phone: {order.shippingAddress.phone}
      </p>

      <h3>Items</h3>
      <ul>
        {order.items.map(item => (
          <li key={item.id}>
            {item.productName} - Qty: {item.quantity} - ₹{item.unitPrice}
          </li>
        ))}
      </ul>

      <button
        onClick={cancelOrder}
        style={{
          marginTop: '20px',
          backgroundColor: 'red',
          color: 'white',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '4px'
        }}
      >
        Cancel Order
      </button>
    </div>
  );
}

export default OrderDetails;
