import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchProduct();
  }, []);

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/${id}`);
      setProduct(res.data);
    } catch (err) {
      console.error('Error fetching product:', err);
      setError('Product not found');
    }
  };

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      await api.post('/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      });
      setMessage('✔️ Product added to cart!');
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      console.error('Add to cart error:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setMessage('❌ Failed to add to cart.');
      }
    }
  };

  if (error) return <div style={{ padding: '20px' }}>{error}</div>;
  if (!product) return <div>Loading...</div>;

  return (
    <div style={{ padding: '40px', display: 'flex', gap: '40px' }}>
      <img
        src={`http://localhost:8080/api/v1/products/image/${product.imageUrl}`}
        onError={(e) => { e.target.src = '/placeholder.png'; }}
        alt={product.name}
        style={{
          width: '300px',
          height: '300px',
          objectFit: 'cover',
          borderRadius: '8px',
          boxShadow: '0 0 10px rgba(0,0,0,0.1)',
        }}
      />
      <div>
        <h2>{product.name}</h2>
        <p style={{ fontSize: '20px', fontWeight: 'bold' }}>₹{product.price}</p>
        <p>{product.description}</p>
        <p><strong>Category:</strong> {product.category?.name}</p>
        <button className="button" onClick={handleAddToCart}>
          Add to Cart
        </button>
        <button className="button" onClick={() => navigate('/')} style={{ marginLeft: '10px' }}>
          ⬅ Back to Home
        </button>
        {message && <p style={{ marginTop: '10px', color: 'green' }}>{message}</p>}
      </div>
    </div>
  );
}

export default ProductDetail;
