import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';
import Footer from '../components/Footer';
import ProductCard from '../components/ProductCard';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [similarProducts, setSimilarProducts] = useState([]);
  const [selectedImage, setSelectedImage] = useState('');
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [selectedSize, setSelectedSize] = useState('');

  const sizeCategories = ['T-shirt', 'T-shirts', 'Shirt', 'Shirts', 'Jacket', 'Jackets'];

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/get/${id}`);
      setProduct(res.data);
      setSelectedImage(res.data.imageUrl);
      const categoryName = typeof res.data.category === 'object' ? res.data.category.name : res.data.category;
      if (categoryName) fetchSimilarProducts(categoryName);
    } catch (err) {
      setError('‚ùå Product not found');
    }
  };

  const fetchSimilarProducts = async (categoryName) => {
    try {
      const res = await api.get(`/v1/products/category/byname?name=${encodeURIComponent(categoryName)}`);
      const filtered = res.data.filter(p => p.id == Number(id));
      setSimilarProducts(filtered);
    } catch (err) {
      console.error('Similar fetch failed:', err);
    }
  };

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/login');

    try {
      await api.post('/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      });
      setMessage('‚úîÔ∏è Added to cart');
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      setMessage('‚ùå Failed to add to cart.');
    }
  };

  const handleBuyNow = () => {
    navigate('/checkout', { state: { product } });
  };

  if (error) return <div style={{ padding: '20px', color: 'red' }}>{error}</div>;
  if (!product) return <div style={{ padding: '20px' }}>Loading...</div>;

  const showSizes = sizeCategories.includes(product.category);

  return (
    <div style={{ padding: '30px 40px' }}>
      {/* Product layout */}
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '40px' }}>
        {/* Left column: images */}
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <img
            src={selectedImage || '/placeholder.png'}
            alt={product.name}
            style={{
              width: 550,
              height: 550,
              objectFit: 'cover',
              borderRadius: '6px',
              border: '1px solid #ddd'
            }}
          />
          {/* Removed color thumbnails */}
          <div style={{ marginTop: '20px' }}>
            <button
              onClick={handleAddToCart}
              style={{
                backgroundColor: '#ff3f6c',
                color: '#fff',
                padding: '12px 25px',
                border: 'none',
                borderRadius: '4px',
                marginRight: '10px',
                cursor: 'pointer',
              }}
            >
              üõí Add to Cart
            </button>
            <button
              onClick={handleBuyNow}
              style={{
                backgroundColor: '#fb641b',
                color: '#fff',
                padding: '12px 25px',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              ‚ö° Buy Now
            </button>
          </div>
        </div>

        {/* Right column: details */}
        <div style={{ flex: 1 }}>
          <h2>{product.name}</h2>
          <p style={{ margin: '10px 0' }}>{product.description || 'No description available'}</p>
          <p style={{ fontSize: '22px', fontWeight: 'bold', color: '#388e3c' }}>‚Çπ{product.price} <span style={{ fontSize: '14px', color: '#999', marginLeft: '10px', textDecoration: 'line-through' }}>‚Çπ{(product.price * 1.78).toFixed(0)}</span> <span style={{ color: '#388e3c' }}>78% off</span></p>

          {/* Size selector */}
          {showSizes && (
            <div style={{ margin: '20px 0' }}>
              <p><strong>Select Size:</strong></p>
              {['S', 'M', 'L', 'XL'].map(size => (
                <button
                  key={size}
                  onClick={() => setSelectedSize(size)}
                  style={{
                    marginRight: '10px',
                    padding: '8px 18px',
                    borderRadius: '4px',
                    border: selectedSize === size ? '2px solid #ff3f6c' : '1px solid #ccc',
                    backgroundColor: selectedSize === size ? '#fff0f5' : '#fff',
                    cursor: 'pointer'
                  }}
                >
                  {size}
                </button>
              ))}
              <span style={{ marginLeft: '15px', fontSize: '12px', color: '#007bff', cursor: 'pointer' }}>Size Chart üìè</span>
            </div>
          )}

          {/* Coupons */}
          <div style={{ backgroundColor: '#f1fff0', padding: '10px 15px', borderRadius: '5px', marginBottom: '15px' }}>
            <p><strong>üí∞ Coupons for you</strong></p>
            <p>‚úÖ Get extra ‚Çπ15 off on 20 items (price inclusive of cashback)</p>
          </div>

          {/* Offers */}
          <div style={{ backgroundColor: '#f9f9f9', padding: '10px 15px', borderRadius: '5px' }}>
            <p><strong>üè¶ Available offers</strong></p>
            <ul style={{ marginLeft: '20px', fontSize: '14px' }}>
              <li>üí≥ Bank Offer 100% Cashback upto 500Rs on Axis Bank SuperMoney Rupay CC UPI transactions on super.money UPI T&C</li>
              <li>üí≥ Bank Offer 5% cashback on Flipkart Axis Bank Credit Card upto ‚Çπ4,000 per statement quarter T&C</li>
              <li>üí≥ Bank Offer 10% instant discount on SBI Credit Card EMI Transactions, up to 1,500 on orders of ‚Çπ5,000 and above T&C</li>
            </ul>
          </div>

          {message && (
            <p style={{ marginTop: '10px', color: message.includes('‚úîÔ∏è') ? 'green' : 'red' }}>{message}</p>
          )}
        </div>
      </div>

      {/* Similar Products */}
      <div style={{ marginTop: '60px' }}>
        <h3>Similar Products</h3>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', marginTop: '20px' }}>
          {similarProducts.length > 0 ? (
            similarProducts.map(p => <ProductCard key={p.id} product={p} />)
          ) : (
            <p>No similar products found.</p>
          )}
        </div>
      </div>

      <Footer />
    </div>
  );
}

export default ProductDetail;
