import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';
import Footer from '../components/Footer';
import ProductCard from '../components/ProductCard';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [similarProducts, setSimilarProducts] = useState([]);
  const [selectedImage, setSelectedImage] = useState('');
  const [error, setError] = useState('');
  const [selectedSize, setSelectedSize] = useState('');

  const sizeCategories = ['T-shirt', 'T-shirts', 'Shirt', 'Shirts', 'Jacket', 'Jackets'];

  useEffect(() => {
    fetchProduct();
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, [id]);

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/get/${id}`);
      setProduct(res.data);
      setSelectedImage(res.data.imageUrl);
      const categoryName = typeof res.data.category === 'object' ? res.data.category.name : res.data.category;
      if (categoryName) fetchSimilarProducts(categoryName);
    } catch (err) {
      setError('❌ Product not found');
    }
  };

  const fetchSimilarProducts = async (categoryName) => {
    try {
      const res = await api.get(`/v1/products/category/byname?name=${encodeURIComponent(categoryName)}`);
      const filtered = res.data.filter(p => p.id !== Number(id));
      setSimilarProducts(filtered);
    } catch (err) {
      console.error('Similar fetch failed:', err);
    }
  };

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) return navigate('/login');

    try {
      await api.post('/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      });
      toast.success('✔️ Added to cart');
    } catch (err) {
      toast.error('❌ Failed to add to cart');
    }
  };

  const handleBuyNow = () => {
    navigate('/checkout', { state: { product } });
  };

  const handleSimilarProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  if (error) {
    return (
      <div style={{ textAlign: 'center', padding: '40px' }}>
        <h2>{error}</h2>
        <button onClick={() => navigate('/')}>Go Back</button>
      </div>
    );
  }

  if (!product) {
    return (
      <div style={{ textAlign: 'center', padding: '40px' }}>
        Loading...
      </div>
    );
  }

  const showSizes = sizeCategories.includes(product.category);

  return (
    <div>
      {/* ... existing layout unchanged ... */}

      {/* Similar Products Section */}
      <div style={{
        background: 'white',
        borderRadius: '20px',
        padding: '40px',
        boxShadow: '0 10px 30px rgba(0,0,0,0.1)',
        marginBottom: '40px'
      }}>
        <h2 style={{
          fontSize: '28px',
          fontWeight: '700',
          color: '#2c3e50',
          margin: '0 0 30px 0',
          display: 'flex',
          alignItems: 'center',
          gap: '12px'
        }}>
          <span>🔍</span>
          Similar Products
        </h2>

        <div className="similar-products-grid">
          {similarProducts.length > 0 ? (
            similarProducts.map(p => (
              <ProductCard
                key={p.id}
                product={p}
                onClick={() => handleSimilarProductClick(p.id)}
              />
            ))
          ) : (
            <div style={{
              gridColumn: '1 / -1',
              textAlign: 'center',
              padding: '40px',
              color: '#7f8c8d'
            }}>
              <h3>No similar products found</h3>
            </div>
          )}
        </div>
      </div>

      <Footer />
      <ToastContainer />
    </div>
  );
}

export default ProductDetail;
