import React, { useState } from 'react';
import { 
  ShoppingCart, 
  CreditCard, 
  MapPin, 
  Package, 
  Lock, 
  User, 
  Home, 
  Phone, 
  Mail,
  CheckCircle,
  Loader2
} from 'lucide-react';

function CheckoutDemo() {
  const [loading, setLoading] = useState(false);
  const [address, setAddress] = useState({
    fullName: '',
    line1: '',
    line2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    phone: '',
  });

  // Sample cart data for demo
  const cart = {
    items: [
      {
        productId: 1,
        productName: "Premium Wireless Headphones",
        quantity: 1,
        price: 2999,
        imageUrl: "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=80&h=80&fit=crop&crop=center"
      },
      {
        productId: 2,
        productName: "Smart Watch Series 5",
        quantity: 2,
        price: 15999,
        imageUrl: "https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=80&h=80&fit=crop&crop=center"
      }
    ],
    total: 34997
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Simulate processing
    setTimeout(() => {
      setLoading(false);
      alert('Demo: Order would be processed here!');
    }, 2000);
  };

  const itemsToDisplay = cart?.items || [];

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      <div className="max-w-6xl w-full mx-auto flex-1 p-4">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center gap-3 mb-2">
            <div className="p-2 bg-blue-100 rounded-lg">
              <ShoppingCart className="h-6 w-6 text-blue-600" />
            </div>
            <h2 className="text-3xl font-bold text-gray-900">
              Checkout
            </h2>
          </div>
          <p className="text-gray-600">Complete your purchase securely</p>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Order Summary */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden sticky top-4">
              <div className="bg-gradient-to-r from-blue-600 to-blue-700 p-6">
                <div className="flex items-center gap-3">
                  <Package className="h-6 w-6 text-white" />
                  <h3 className="text-xl font-semibold text-white">Order Summary</h3>
                </div>
              </div>

              <div className="p-6">
                {itemsToDisplay.length > 0 ? (
                  <div className="space-y-4">
                    {itemsToDisplay.map(item => (
                      <div key={item.productId} className="flex items-center gap-4 p-4 bg-gray-50 rounded-xl border border-gray-100">
                        <div className="relative">
                          <img
                            src={item.imageUrl}
                            alt={item.productName}
                            className="w-16 h-16 object-cover rounded-lg shadow-sm"
                            onError={(e) => (e.target.src = 'https://via.placeholder.com/80')}
                          />
                          <div className="absolute -top-2 -right-2 bg-blue-600 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center font-semibold">
                            {item.quantity}
                          </div>
                        </div>
                        <div className="flex-1">
                          <h4 className="font-semibold text-gray-900 mb-1">{item.productName}</h4>
                          <p className="text-sm text-gray-500 mb-2">Quantity: {item.quantity}</p>
                          <p className="font-bold text-blue-600 text-lg">₹{item.price * item.quantity}</p>
                        </div>
                      </div>
                    ))}

                    <div className="border-t pt-4 mt-6">
                      <div className="flex justify-between items-center p-4 bg-gradient-to-r from-green-50 to-blue-50 rounded-xl">
                        <span className="text-lg font-semibold text-gray-900">Total Amount:</span>
                        <span className="text-2xl font-bold text-blue-600">₹{cart?.total || 0}</span>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <ShoppingCart className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-500">No items in the cart.</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Shipping & Payment */}
          <div className="lg:col-span-2">
            <div className="space-y-6">
              {/* Shipping Address */}
              <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
                <div className="bg-gradient-to-r from-green-600 to-green-700 p-6">
                  <div className="flex items-center gap-3">
                    <MapPin className="h-6 w-6 text-white" />
                    <h3 className="text-xl font-semibold text-white">Shipping Address</h3>
                  </div>
                </div>

                <div className="p-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="md:col-span-2">
                      <Input 
                        label="Full Name" 
                        value={address.fullName} 
                        onChange={val => setAddress({ ...address, fullName: val })} 
                        required 
                        icon={<User className="h-4 w-4" />}
                      />
                    </div>
                    <Input 
                      label="Address Line 1" 
                      value={address.line1} 
                      onChange={val => setAddress({ ...address, line1: val })} 
                      required 
                      icon={<Home className="h-4 w-4" />}
                    />
                    <Input 
                      label="Address Line 2 (Optional)" 
                      value={address.line2} 
                      onChange={val => setAddress({ ...address, line2: val })} 
                      icon={<Home className="h-4 w-4" />}
                    />
                    <Input 
                      label="City" 
                      value={address.city} 
                      onChange={val => setAddress({ ...address, city: val })} 
                      required 
                      icon={<MapPin className="h-4 w-4" />}
                    />
                    <Input 
                      label="State" 
                      value={address.state} 
                      onChange={val => setAddress({ ...address, state: val })} 
                      required 
                      icon={<MapPin className="h-4 w-4" />}
                    />
                    <Input 
                      label="Postal Code" 
                      value={address.postalCode} 
                      onChange={val => setAddress({ ...address, postalCode: val })} 
                      required 
                      icon={<Mail className="h-4 w-4" />}
                    />
                    <Input 
                      label="Country" 
                      value={address.country} 
                      onChange={val => setAddress({ ...address, country: val })} 
                      required 
                      icon={<MapPin className="h-4 w-4" />}
                    />
                    <Input 
                      label="Phone Number" 
                      value={address.phone} 
                      onChange={val => setAddress({ ...address, phone: val })} 
                      required 
                      icon={<Phone className="h-4 w-4" />}
                    />
                  </div>
                </div>
              </div>

              {/* Payment Details */}
              <div className="bg-white rounded-2xl shadow-lg border border-gray-100 overflow-hidden">
                <div className="bg-gradient-to-r from-purple-600 to-purple-700 p-6">
                  <div className="flex items-center gap-3">
                    <CreditCard className="h-6 w-6 text-white" />
                    <h3 className="text-xl font-semibold text-white">Payment Details</h3>
                  </div>
                </div>

                <div className="p-6">
                  <div className="border-2 border-dashed border-gray-200 rounded-xl p-6 bg-gray-50 hover:bg-gray-100 transition-colors">
                    <div className="flex items-center gap-2 mb-3">
                      <Lock className="h-4 w-4 text-green-600" />
                      <span className="text-sm font-medium text-gray-700">Secure payment powered by Stripe</span>
                    </div>
                    <div className="p-4 bg-white rounded-lg border border-gray-200">
                      <p className="text-gray-500 text-sm">Card Element would be rendered here</p>
                    </div>
                  </div>
                </div>
              </div>

              {/* Submit Button */}
              <div className="bg-white rounded-2xl shadow-lg border border-gray-100 p-6">
                <button
                  type="button"
                  onClick={handleSubmit}
                  disabled={loading}
                  className={`w-full py-4 px-6 rounded-xl text-white font-semibold text-lg transition-all duration-200 transform ${
                    loading 
                      ? 'bg-gray-400 cursor-not-allowed' 
                      : 'bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 hover:scale-105 shadow-lg hover:shadow-xl'
                  }`}
                >
                  <div className="flex items-center justify-center gap-3">
                    {loading ? (
                      <>
                        <Loader2 className="h-5 w-5 animate-spin" />
                        <span>Processing Payment...</span>
                      </>
                    ) : (
                      <>
                        <CheckCircle className="h-5 w-5" />
                        <span>Complete Payment - ₹{cart?.total || 0}</span>
                      </>
                    )}
                  </div>
                </button>

                <div className="flex items-center justify-center gap-2 mt-4 text-sm text-gray-500">
                  <Lock className="h-4 w-4" />
                  <span>Your payment information is secure and encrypted</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer Placeholder */}
      <div className="bg-gray-800 text-white py-8 mt-12">
        <div className="max-w-6xl mx-auto px-4 text-center">
          <p>&copy; 2024 Your Store. All rights reserved.</p>
        </div>
      </div>
    </div>
  );
}

const Input = ({ label, value, onChange, required = false, icon }) => (
  <div className="flex flex-col">
    <label className="text-sm font-semibold mb-2 text-gray-700 flex items-center gap-2">
      {icon && <span className="text-gray-500">{icon}</span>}
      {label} 
      {required && <span className="text-red-500">*</span>}
    </label>
    <div className="relative">
      <input
        type="text"
        value={value}
        onChange={(e) => onChange(e.target.value)}
        required={required}
        className="w-full border-2 border-gray-200 rounded-lg px-4 py-3 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 bg-white hover:border-gray-300"
        placeholder={`Enter ${label.toLowerCase()}`}
      />
    </div>
  </div>
);

export default CheckoutDemo;
