import React, { useEffect, useState } from 'react';
import { Package, Calendar, CreditCard, Eye, ShoppingBag } from 'lucide-react';

// Mock components for demonstration
const Footer = () => <div className="text-center text-gray-500 py-4">© 2024 Your Store</div>;

// Mock API and navigation for demonstration
const api = {
  get: async (url) => {
    // Simulate API call with mock data
    return new Promise((resolve) => {
      setTimeout(() => {
        resolve({
          data: [
            {
              id: '12345',
              status: 'delivered',
              total: '2,450',
              createdAt: '2024-06-20T10:30:00Z'
            },
            {
              id: '12346',
              status: 'processing',
              total: '1,280',
              createdAt: '2024-06-22T14:15:00Z'
            },
            {
              id: '12347',
              status: 'pending',
              total: '850',
              createdAt: '2024-06-24T09:45:00Z'
            }
          ]
        });
      }, 500);
    });
  }
};

const useNavigate = () => {
  return (path) => {
    console.log(`Navigate to: ${path}`);
    alert(`Would navigate to: ${path}`);
  };
};

function MyOrders() {
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await api.get('/orders/user');
        setOrders(res.data);
      } catch (err) {
        console.error('Failed to fetch orders', err);
        alert('Error loading your orders');
      }
    };

    fetchOrders();
  }, []);

  const getStatusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'processing':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'shipped':
        return 'bg-purple-100 text-purple-800 border-purple-200';
      case 'delivered':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'cancelled':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      <div className="max-w-4xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-blue-100 rounded-full mb-4">
            <ShoppingBag className="w-8 h-8 text-blue-600" />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">My Orders</h1>
          <p className="text-gray-600">Track and manage your order history</p>
        </div>

        {orders.length === 0 ? (
          <div className="text-center py-16">
            <div className="inline-flex items-center justify-center w-24 h-24 bg-gray-100 rounded-full mb-6">
              <Package className="w-12 h-12 text-gray-400" />
            </div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">No Orders Yet</h3>
            <p className="text-gray-600 mb-6">You haven't placed any orders yet.</p>
            <button
              onClick={() => navigate('/products')}
              className="inline-flex items-center px-6 py-3 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition-colors duration-200"
            >
              <ShoppingBag className="w-5 h-5 mr-2" />
              Start Shopping
            </button>
          </div>
        ) : (
          <div className="space-y-6">
            {orders.map(order => (
              <div
                key={order.id}
                className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden hover:shadow-md transition-shadow duration-200"
              >
                <div className="p-6">
                  {/* Order Header */}
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex items-center space-x-3">
                      <div className="flex items-center justify-center w-10 h-10 bg-blue-100 rounded-lg">
                        <Package className="w-5 h-5 text-blue-600" />
                      </div>
                      <div>
                        <h3 className="text-lg font-semibold text-gray-900">
                          Order #{order.id}
                        </h3>
                        <div className="flex items-center text-sm text-gray-500 mt-1">
                          <Calendar className="w-4 h-4 mr-1" />
                          {new Date(order.createdAt).toLocaleString()}
                        </div>
                      </div>
                    </div>
                    <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium border ${getStatusColor(order.status)}`}>
                      {order.status}
                    </span>
                  </div>

                  {/* Order Details */}
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                      <CreditCard className="w-5 h-5 text-gray-400" />
                      <span className="text-lg font-bold text-gray-900">₹{order.total}</span>
                    </div>
                    <button
                      onClick={() => navigate(`/order/${order.id}`)}
                      className="inline-flex items-center px-4 py-2 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors duration-200"
                    >
                      <Eye className="w-4 h-4 mr-2" />
                      View Details
                    </button>
                  </div>
                </div>

                {/* Progress Bar for Status */}
                <div className="px-6 pb-4">
                  <div className="w-full bg-gray-200 rounded-full h-1">
                    <div 
                      className={`h-1 rounded-full transition-all duration-300 ${
                        order.status?.toLowerCase() === 'pending' ? 'bg-yellow-500 w-1/4' :
                        order.status?.toLowerCase() === 'processing' ? 'bg-blue-500 w-2/4' :
                        order.status?.toLowerCase() === 'shipped' ? 'bg-purple-500 w-3/4' :
                        order.status?.toLowerCase() === 'delivered' ? 'bg-green-500 w-full' :
                        'bg-gray-400 w-1/4'
                      }`}
                    ></div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Footer Spacing */}
        <div className="mt-16">
          <Footer />
        </div>
      </div>
    </div>
  );
}

export default MyOrders;
