import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../services/api';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/auth/login', { email, password });
      const { token, userId, role } = res.data;

      if (token) {
        localStorage.setItem('token', token);
        localStorage.setItem('userId', userId);
        localStorage.setItem('role', role);
        console.log('✅ Token stored:', token);
        if (role === 'ADMIN') {
          navigate('/admin-dashboard');
        } else {
          navigate('/');
        }
        window.location.reload();
      } else {
        console.error('⚠️ No token received');
        alert('Login failed. No token received.');
      }
    } catch (err) {
      console.error('❌ Login error:', err.response?.data || err.message);
      alert('Login failed. Please check your credentials.');
    }
  };

  const handleGoogleLogin = () => {
    // Replace with actual Google login route
    window.location.href = 'http://localhost:8080/oauth2/authorization/google';
  };

  return (
    <div className="container" style={{ maxWidth: '400px', marginTop: '50px' }}>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '10px' }}>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ width: '100%', padding: '8px', marginTop: '5px' }}
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: '100%', padding: '8px', marginTop: '5px' }}
          />
        </div>
        <div style={{ marginBottom: '10px', textAlign: 'right' }}>
          <Link to="/forgot-password" style={{ fontSize: '0.9rem' }}>Forgot Password?</Link>
        </div>
        <button className="button" type="submit" style={{ width: '100%' }}>
          Login
        </button>
      </form>

      <div style={{ textAlign: 'center', margin: '20px 0' }}>— or —</div>
      <button onClick={handleGoogleLogin} className="button" style={{ width: '100%', background: '#4285F4', color: 'white' }}>
        Continue with Google
      </button>
    </div>
  );
}

export default Login;
