import React, { useState } from 'react';
import api from '../services/api'; // Uses interceptor to add token

function AdminDashboard() {
  const [activeSection, setActiveSection] = useState('add-product');
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    brand: '',
    price: '',
    category: '',
    image: null,
    currentName: '',
    newName: '',
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({ ...formData, [name]: files ? files[0] : value });
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.name);
    data.append('description', formData.description);
    data.append('brand', formData.brand);
    data.append('price', formData.price);
    data.append('category', formData.category);
    data.append('image', formData.image);

    try {
      await api.post('/v1/products/addproduct', data, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert('Product added successfully');
    } catch (err) {
      console.error(err);
      alert('Failed to add product');
    }
  };

  const handleUpdateProduct = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('name', formData.currentName);
    data.append('newName', formData.newName);
    data.append('description', formData.description);
    data.append('brand', formData.brand);
    data.append('price', formData.price);
    data.append('category', formData.category);
    if (formData.image) data.append('image', formData.image);

    try {
      await api.put('/v1/products/update', data, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert('Product updated successfully');
    } catch (err) {
      console.error(err);
      alert('Failed to update product');
    }
  };

  const handleDeleteProduct = async (e) => {
    e.preventDefault();
    try {
      await api.delete('/v1/products/delete', {
        params: { name: formData.name },
      });
      alert('Product deleted successfully');
    } catch (err) {
      console.error(err);
      alert('Failed to delete product');
    }
  };

  const renderContent = () => {
    switch (activeSection) {
      case 'add-product':
        return (
          <div>
            <h3>Add Product</h3>
            <form onSubmit={handleAddProduct} style={formStyle}>
              <input type="text" name="name" placeholder="Product Name" required onChange={handleChange} />
              <textarea name="description" placeholder="Description" onChange={handleChange} />
              <input type="text" name="brand" placeholder="Brand" required onChange={handleChange} />
              <input type="number" name="price" placeholder="Price" required onChange={handleChange} />
              <input type="text" name="category" placeholder="Category" required onChange={handleChange} />
              <input type="file" name="image" accept="image/*" onChange={handleChange} />
              <button type="submit" className="button">Add Product</button>
            </form>
          </div>
        );

      case 'update-product':
        return (
          <div>
            <h3>Update Product</h3>
            <form onSubmit={handleUpdateProduct} style={formStyle}>
              <input type="text" name="currentName" placeholder="Current Product Name" required onChange={handleChange} />
              <input type="text" name="newName" placeholder="New Product Name" onChange={handleChange} />
              <textarea name="description" placeholder="New Description" onChange={handleChange} />
              <input type="text" name="brand" placeholder="New Brand" onChange={handleChange} />
              <input type="number" name="price" placeholder="New Price" onChange={handleChange} />
              <input type="text" name="category" placeholder="New Category" onChange={handleChange} />
              <input type="file" name="image" accept="image/*" onChange={handleChange} />
              <button type="submit" className="button">Update Product</button>
            </form>
          </div>
        );

      case 'remove-product':
        return (
          <div>
            <h3>Remove Product</h3>
            <form onSubmit={handleDeleteProduct} style={formStyle}>
              <input type="text" name="name" placeholder="Product Name" required onChange={handleChange} />
              <button className="button" type="submit" style={{ backgroundColor: '#ff3f6c', color: 'white' }}>
                Remove Product
              </button>
            </form>
          </div>
        );

      case 'access-users':
        return <div><h3>Access Users</h3><p>Coming soon.</p></div>;

      case 'user-orders':
        return <div><h3>User Orders</h3><p>Coming soon.</p></div>;

      default:
        return null;
    }
  };

  return (
    <div style={{ display: 'flex', minHeight: '100vh' }}>
      <div style={sidebarStyle}>
        <h3>Admin Panel</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {['add-product', 'update-product', 'remove-product', 'access-users', 'user-orders'].map((section) => (
            <li key={section}>
              <button
                onClick={() => setActiveSection(section)}
                style={getButtonStyle(activeSection === section)}
              >
                {section.replace('-', ' ').replace(/\b\w/g, (l) => l.toUpperCase())}
              </button>
            </li>
          ))}
        </ul>
      </div>
      <div style={{ flex: 1, padding: '20px' }}>{renderContent()}</div>
    </div>
  );
}

const sidebarStyle = {
  width: '250px',
  backgroundColor: '#f5f5f5',
  padding: '20px',
  boxShadow: '2px 0 5px rgba(0,0,0,0.1)',
};

const getButtonStyle = (isActive) => ({
  width: '100%',
  padding: '10px',
  textAlign: 'left',
  background: isActive ? '#ff3f6c' : 'none',
  color: isActive ? '#fff' : '#333',
  border: 'none',
  cursor: 'pointer',
  marginBottom: '5px',
  fontWeight: isActive ? 'bold' : 'normal',
  borderRadius: '4px',
});

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '10px',
  maxWidth: '500px',
};

export default AdminDashboard;
