import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';
import { FaHeart, FaRegHeart } from 'react-icons/fa';

function ProductCard({ product }) {
  const navigate = useNavigate();
  const [hovered, setHovered] = useState(false);
  const [isWishlisted, setIsWishlisted] = useState(false);

  const token = localStorage.getItem('token');

  // Fetch wishlist on mount (optional if globally fetched)
  useEffect(() => {
    const fetchWishlist = async () => {
      if (!token) return;
      try {
        const res = await api.get('/api/v1/wishlist/list');
        const wishlistedIds = res.data.map((p) => p.id);
        setIsWishlisted(wishlistedIds.includes(product.id));
      } catch (err) {
        console.error('Failed to fetch wishlist', err);
      }
    };
    fetchWishlist();
  }, [product.id, token]);

  const toggleWishlist = async () => {
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      if (isWishlisted) {
        await api.delete(`/api/v1/wishlist/remove/${product.id}`);
        setIsWishlisted(false);
      } else {
        await api.post(`/api/v1/wishlist/add/${product.id}`);
        setIsWishlisted(true);
      }
    } catch (err) {
      console.error('Wishlist error:', err);
    }
  };

  const handleAddToCart = async () => {
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      await api.post('/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      });
      alert('✔️ Product added to cart!');
    } catch (err) {
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        console.error('❌ Error adding to cart:', err);
        alert('❌ Something went wrong while adding to cart.');
      }
    }
  };

  if (!product || typeof product !== 'object') return null;

  const imageSrc = product.imageUrl?.startsWith('http')
    ? product.imageUrl
    : `http://localhost:8080/api/v1/products/image/${product.imageUrl}`;

  return (
    <div
      style={{
        border: '1px solid #e0e0e0',
        borderRadius: '8px',
        background: 'white',
        padding: '12px',
        margin: '10px',
        width: '220px',
        boxShadow: '0 2px 6px rgba(0, 0, 0, 0.05)',
        position: 'relative',
      }}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <Link to={`/product/${product.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
        <img
          src={imageSrc}
          onError={(e) => { e.target.src = '/placeholder.png'; }}
          alt={product.name}
          style={{
            width: '100%',
            height: '200px',
            objectFit: 'cover',
            borderRadius: '4px',
          }}
        />
        <h3 style={{ fontSize: '16px', margin: '10px 0 5px' }}>{product.name}</h3>
        <p style={{ color: '#666', fontWeight: 'bold' }}>₹{product.price}</p>
      </Link>

      {/* Wishlist icon */}
      {hovered && (
        <div
          style={{
            position: 'absolute',
            top: '8px',
            right: '8px',
            cursor: 'pointer',
            fontSize: '20px',
            color: isWishlisted ? '#ff4081' : '#999',
            background: 'white',
            borderRadius: '50%',
            padding: '5px',
            boxShadow: '0 1px 4px rgba(0,0,0,0.2)',
          }}
          onClick={toggleWishlist}
        >
          {isWishlisted ? <FaHeart /> : <FaRegHeart />}
        </div>
      )}

      <button
        className="button"
        onClick={handleAddToCart}
        style={{
          marginTop: '10px',
          width: '100%',
          padding: '8px',
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
        }}
      >
        Add to Cart
      </button>
    </div>
  );
}

export default ProductCard;
