import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function Cart() {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // ... (rest of the existing code for fetchCart, handleRemove, handleClear, handleUpdateQuantity remains unchanged)

  if (loading) return <div>Loading cart...</div>;

  if (error) {
    return (
      <div style={{ padding: '20px', color: 'red' }}>
        <p>{error}</p>
      </div>
    );
  }

  if (!cart || !cart.items) return <div>No cart data.</div>;

  return (
    <div className="container">
      <h2>Your Cart</h2>
      {cart.items.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          {cart.items.map((item) => (
            <div
              key={item.itemId}
              style={{
                display: 'flex',
                alignItems: 'center',
                marginBottom: '10px',
                background: 'white',
                padding: '10px',
                borderRadius: '4px',
              }}
            >
              <img
                src={
                  item.product?.imageUrl
                    ? `http://localhost:8080/api/v1/products/image/${item.product.imageUrl}`
                    : '/placeholder.png'
                }
                alt={item.product?.name || 'Product Image'}
                style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                onError={(e) => {
                  e.target.src = '/placeholder.png';
                }}
              />
              <div style={{ marginLeft: '20px', flex: 1 }}>
                <h4
                  style={{ cursor: 'pointer', color: '#007bff' }}
                  onClick={() => navigate(`/product/${item.product.id}`)}
                >
                  {item.product?.name || 'Unknown Product'}
                </h4>
                <p>Price: ₹{item.product?.price || 'N/A'}</p>
                <p>Subtotal: ₹{item.subtotal || 'N/A'}</p>
                <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                  <button
                    className="button"
                    onClick={() => handleUpdateQuantity(item.itemId, item.quantity - 1)}
                    disabled={item.quantity <= 1}
                  >
                    -
                  </button>
                  <span>Qty: {item.quantity}</span>
                  <button
                    className="button"
                    onClick={() => handleUpdateQuantity(item.itemId, item.quantity + 1)}
                  >
                    +
                  </button>
                  <button
                    className="button"
                    onClick={() => handleRemove(item.itemId)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            </div>
          ))}
          <h3>Total: ₹{cart.total || 0}</h3>
          <button
            className="button"
            onClick={() => navigate('/checkout')}
            style={{ marginTop: '10px' }}
          >
            Proceed to Checkout
          </button>
          <button
            className="button"
            onClick={handleClear}
            style={{ marginTop: '10px', marginLeft: '10px' }}
          >
            Clear Cart
          </button>
        </div>
      )}
    </div>
  );
}

export default Cart;
