import React from 'react';
import Slider from 'react-slick';
import { useNavigate } from 'react-router-dom';
import './CategorySlider.css';

const CategorySlider = ({ onBannerClick }) => {
  const navigate = useNavigate();

  const banners = [
    { name: 'T-shirts', img: '/images/tshirts.jpg' },
    { name: 'Jeans', img: '/images/jeans1.jpg' }
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    cssEase: 'ease-in-out'
  };

  const handleClick = (category) => {
    if (onBannerClick) onBannerClick(category);
    else navigate(`/category/${category}`);
  };

  return (
    <div className="category-slider">
      <Slider {...settings}>
        {banners.map((banner, i) => (
          <div key={i} className="slide" onClick={() => handleClick(banner.name)}>
            <img src={banner.img} alt={banner.name} />
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default CategorySlider;
