import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function ProductDetail() {
  const { id } = useParams(); // Get product ID from URL
  const navigate = useNavigate();
  const [product, setProduct] = React.useState(null);

  // Fetch product details (assumed to exist)
  React.useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await api.get(`/products/${id}`); // Adjust endpoint as per your API
        setProduct(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchProduct();
  }, [id]);

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      // User is not logged in, redirect to login page
      navigate('/login');
      return;
    }

    try {
      // Make API call to add item to cart
      const request = { productId: id, quantity: 1 }; // Adjust quantity as needed
      await api.post('/v1/carts/items', request, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      alert('Product added to cart!');
      // Optionally, navigate to cart page
      navigate('/cart');
    } catch (err) {
      console.error('Add to cart error:', err.response?.data || err);
      if (err.response?.status === 401) {
        // Token is invalid or expired, clear token and redirect to login
        localStorage.removeItem('token');
        localStorage.removeItem('userId');
        navigate('/login');
      } else {
        alert('Failed to add product to cart');
      }
    }
  };

  if (!product) return <div>Loading...</div>;

  return (
    <div className="container">
      <h2>{product.name}</h2>
      <img src={product.imageUrl} alt={product.name} style={{ width: '200px', height: '200px', objectFit: 'cover' }} />
      <p>Price: â‚¹{product.price}</p>
      <p>{product.description}</p>
      <button className="button" onClick={handleAddToCart}>
        Add to Cart
      </button>
    </div>
  );
}

export default ProductDetail;
