import React, { useState } from 'react';

function AdminDashboard() {
  const [activeSection, setActiveSection] = useState('add-product');

  const renderContent = () => {
    switch (activeSection) {
      case 'add-product':
        return (
          <div>
            <h3>Add Product</h3>
            <form style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '500px' }}>
              <input type="text" placeholder="Product Name" required />
              <textarea placeholder="Description" />
              <input type="text" placeholder="Brand" required />
              <input type="number" placeholder="Price" required />
              <input type="text" placeholder="Category" required />
              <input type="file" accept="image/*" />
              <button className="button" type="submit">Add Product</button>
            </form>
          </div>
        );

      case 'update-product':
        return (
          <div>
            <h3>Update Product</h3>
            <form style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '500px' }}>
              <input type="text" placeholder="Current Product Name" required />
              <input type="text" placeholder="New Product Name" />
              <textarea placeholder="New Description" />
              <input type="text" placeholder="New Brand" />
              <input type="number" placeholder="New Price" />
              <input type="text" placeholder="New Category" />
              <input type="file" accept="image/*" />
              <button className="button" type="submit">Update Product</button>
            </form>
          </div>
        );

      case 'remove-product':
        return (
          <div>
            <h3>Remove Product</h3>
            <form style={{ display: 'flex', flexDirection: 'column', gap: '10px', maxWidth: '500px' }}>
              <input type="text" placeholder="Product Name" required />
              <button className="button" type="submit" style={{ backgroundColor: '#ff3f6c', color: 'white' }}>
                Remove Product
              </button>
            </form>
          </div>
        );

      case 'access-users':
        return (
          <div>
            <h3>Access Users</h3>
            <p>Placeholder for user management (list, edit, delete users).</p>
          </div>
        );

      case 'user-orders':
        return (
          <div>
            <h3>User Orders</h3>
            <p>Placeholder for viewing and managing user orders.</p>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div style={{ display: 'flex', minHeight: '100vh' }}>
      {/* Sidebar */}
      <div style={{
        width: '250px',
        backgroundColor: '#f5f5f5',
        padding: '20px',
        boxShadow: '2px 0 5px rgba(0,0,0,0.1)'
      }}>
        <h3>Admin Panel</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          <li>
            <button
              onClick={() => setActiveSection('add-product')}
              style={getButtonStyle(activeSection === 'add-product')}
            >
              Add Product
            </button>
          </li>
          <li>
            <button
              onClick={() => setActiveSection('update-product')}
              style={getButtonStyle(activeSection === 'update-product')}
            >
              Update Product
            </button>
          </li>
          <li>
            <button
              onClick={() => setActiveSection('remove-product')}
              style={getButtonStyle(activeSection === 'remove-product')}
            >
              Remove Product
            </button>
          </li>
          <li>
            <button
              onClick={() => setActiveSection('access-users')}
              style={getButtonStyle(activeSection === 'access-users')}
            >
              Access Users
            </button>
          </li>
          <li>
            <button
              onClick={() => setActiveSection('user-orders')}
              style={getButtonStyle(activeSection === 'user-orders')}
            >
              User Orders
            </button>
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: '20px' }}>
        {renderContent()}
      </div>
    </div>
  );
}

const getButtonStyle = (isActive) => ({
  width: '100%',
  padding: '10px',
  textAlign: 'left',
  background: isActive ? '#ff3f6c' : 'none',
  color: isActive ? '#fff' : '#333',
  border: 'none',
  cursor: 'pointer',
  marginBottom: '5px',
  fontWeight: isActive ? 'bold' : 'normal',
  borderRadius: '4px'
});

export default AdminDashboard;
