import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import {
  Elements,
  CardElement,
  useStripe,
  useElements
} from '@stripe/react-stripe-js';
import api from '../services/api';
import Footer from '../components/Footer';
import { toast } from 'react-toastify';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const location = useLocation();
  const { product } = location.state || {};

  const [cart, setCart] = useState(null);
  const [savedAddresses, setSavedAddresses] = useState([]);
  const [selectedAddressId, setSelectedAddressId] = useState(null);
  const [loading, setLoading] = useState(false);

  const [address, setAddress] = useState({
    fullName: '',
    line1: '',
    line2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    phone: ''
  });

  // Fetch Cart or Product
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await api.get('/v1/carts');
        if (!res.data?.items?.length) {
          toast.warn('Your cart is empty.');
          navigate('/cart');
          return;
        }
        setCart(res.data);
      } catch (err) {
        toast.error('Error loading cart.');
        navigate('/cart');
      }
    };

    if (!product) {
      fetchCart();
    }
  }, [product, navigate]);

  // Fetch Addresses
  useEffect(() => {
    const fetchAddresses = async () => {
      try {
        const res = await api.get('/api/addresses');
        setSavedAddresses(res.data);
      } catch (err) {
        console.error('Failed to fetch addresses', err);
      }
    };
    fetchAddresses();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const cardElement = elements.getElement(CardElement);
      if (!stripe || !elements || !cardElement) {
        throw new Error('Stripe is not initialized.');
      }

      const amount = product ? Math.round(product.price * 100) : Math.round(cart.total * 100);
      const cartItems = product
        ? [{ productId: product.id, quantity: 1 }]
        : cart.items.map(item => ({
            productId: item.productId,
            quantity: item.quantity
          }));

      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        amount,
        currency: 'inr',
        paymentMethodId: 'pm_card_visa'
      });

      const { clientSecret, paymentIntentId } = paymentIntentRes.data;

      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: cardElement }
      });

      if (error) {
        throw new Error(`Payment failed: ${error.message}`);
      }

      const shippingAddress = {
        fullName: address.fullName,
        street: address.line1,
        city: address.city,
        state: address.state,
        zipCode: address.postalCode,
        country: address.country,
        phone: address.phone
      };

      const checkoutRes = await api.post('/payment/checkout', {
        paymentIntentId: paymentIntent.id,
        shippingAddress,
        items: cartItems,
        isBuyNow: !!product,
        ...(selectedAddressId && { addressId: selectedAddressId })
      });

      toast.success(`Order placed successfully! Order ID: ${checkoutRes.data.orderId}`);
      navigate(`/order/${checkoutRes.data.orderId}`, { state: { isBuyNow: !!product } });
    } catch (err) {
      console.error(err);
      toast.error(err.message || 'Checkout failed.');
    } finally {
      setLoading(false);
    }
  };

  if (!product && !cart) return <div>Loading cart...</div>;

  return (
    <div className="container" style={{ maxWidth: '1200px', minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
      <div style={{ flex: 1 }}>
        <h2 style={{ fontSize: '28px', fontWeight: '600', color: '#333', marginBottom: '20px' }}>
          Checkout {product ? `- ${product.name}` : ''}
        </h2>

        <form onSubmit={handleSubmit}>
          <h3 style={{ fontSize: '20px', fontWeight: '600', marginBottom: '10px' }}>Select Shipping Address</h3>

          {savedAddresses.length > 0 ? (
            savedAddresses.map((addr) => (
              <div
                key={addr.id}
                style={{
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                  padding: '10px',
                  marginBottom: '10px'
                }}
              >
                <label>
                  <input
                    type="radio"
                    name="address"
                    value={addr.id}
                    checked={selectedAddressId === addr.id}
                    onChange={() => {
                      setSelectedAddressId(addr.id);
                      setAddress({
                        fullName: addr.fullName,
                        line1: addr.line1,
                        line2: addr.line2 || '',
                        city: addr.city,
                        state: addr.state,
                        postalCode: addr.postalCode,
                        country: addr.country,
                        phone: addr.phone
                      });
                    }}
                  />
                  <span style={{ marginLeft: '10px' }}>
                    {addr.fullName}, {addr.line1}, {addr.line2 && `${addr.line2}, `}
                    {addr.city}, {addr.state}, {addr.postalCode}, {addr.country} - {addr.phone}
                  </span>
                </label>
              </div>
            ))
          ) : (
            <p>No saved addresses. Please enter one below.</p>
          )}

          <h3 style={{ fontSize: '20px', fontWeight: '600', marginBottom: '10px' }}>Or Enter New Shipping Address</h3>
          <fieldset disabled={!!selectedAddressId} style={{ border: 'none', padding: 0 }}>
            {/* Address Inputs */}
            {[
              { label: 'Full Name', key: 'fullName' },
              { label: 'Line 1', key: 'line1' },
              { label: 'Line 2', key: 'line2' },
              { label: 'City', key: 'city' },
              { label: 'State', key: 'state' },
              { label: 'Postal Code', key: 'postalCode' },
              { label: 'Country', key: 'country' },
              { label: 'Phone', key: 'phone' }
            ].map(({ label, key }) => (
              <div key={key} style={{ marginBottom: '12px' }}>
                <label style={{ fontWeight: 'bold' }}>{label}:</label>
                <input
                  type={key === 'phone' ? 'tel' : 'text'}
                  required={key !== 'line2'}
                  value={address[key]}
                  onChange={(e) => setAddress({ ...address, [key]: e.target.value })}
                  style={{
                    width: '100%',
                    padding: '8px',
                    border: '1px solid #ccc',
                    borderRadius: '4px'
                  }}
                />
              </div>
            ))}
          </fieldset>

          <h3 style={{ fontSize: '20px', fontWeight: '600', marginBottom: '10px' }}>Payment Details</h3>
          <div style={{ padding: '12px', border: '1px solid #ccc', borderRadius: '4px', marginBottom: '20px' }}>
            <CardElement />
          </div>

          <button
            type="submit"
            disabled={!stripe || loading}
            style={{
              marginTop: '20px',
              padding: '10px 20px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '4px',
              cursor: loading ? 'not-allowed' : 'pointer',
              width: '100%'
            }}
          >
            {loading ? 'Processing...' : `Pay ₹${product ? product.price : cart?.total || 0}`}
          </button>
        </form>
      </div>
      <div style={{ marginTop: 'auto', padding: '20px' }}>
        <Footer />
      </div>
    </div>
  );
}

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
