import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import api from '../services/api';
import Footer from '../components/Footer';
import { toast } from 'react-toastify';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const location = useLocation();
  const { product } = location.state || {};

  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);
  const [address, setAddress] = useState({
    fullName: '',
    line1: '',
    line2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    phone: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (product) {
          const res = await api.get(`/v1/products/get/${product.id}`);
          const normalizeProduct = (data) => ({
            id: data.id,
            name: data.name,
            price: data.price,
            imageUrl: data.imageUrl || data.image || 'https://via.placeholder.com/80',
          });
          const productData = normalizeProduct(res.data);

          setCart({
            items: [{
              productId: productData.id,
              productName: productData.name,
              quantity: 1,
              price: productData.price,
              imageUrl: productData.imageUrl,
            }],
            total: productData.price,
          });
        } else {
          const res = await api.get('/v1/carts');
          const updatedItems = await Promise.all(
            res.data.items.map(async (item) => {
              const productRes = await api.get(`/v1/products/get/${item.productId}`);
              return {
                ...item,
                productName: productRes.data.name,
                imageUrl: productRes.data.imageUrl || productRes.data.image || 'https://via.placeholder.com/80',
              };
            })
          );
          setCart({ ...res.data, items: updatedItems });
        }
      } catch (err) {
        toast.error('Error loading data.');
      }
    };
    fetchData();
  }, [product]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const cardElement = elements.getElement(CardElement);
      if (!stripe || !elements || !cardElement) {
        toast.error('Stripe not ready.');
        setLoading(false);
        return;
      }

      const amount = Math.round(cart.total * 100);
      const cartItems = cart.items.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
      }));

      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        amount,
        currency: 'usd',
        paymentMethodId: 'pm_card_visa',
      });

      const { clientSecret, paymentIntentId } = paymentIntentRes.data;

      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: cardElement },
      });

      if (error) {
        toast.error(`Payment failed: ${error.message}`);
        setLoading(false);
        return;
      }

      const shippingAddress = {
        fullName: address.fullName,
        street: address.line1,
        city: address.city,
        state: address.state,
        zipCode: address.postalCode,
        country: address.country,
        phone: address.phone,
      };

      const checkoutRes = await api.post('/payment/checkout', {
        paymentIntentId: paymentIntent.id,
        shippingAddress,
        items: cartItems,
      });

      toast.success(`Order placed! Order ID: ${checkoutRes.data.orderId}`);
      navigate(`/order/${checkoutRes.data.orderId}`);
    } catch (err) {
      console.error(err);
      toast.error(err.response?.data?.error || 'Checkout failed.');
    } finally {
      setLoading(false);
    }
  };

  if (!cart) return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="text-xl font-medium text-gray-600 animate-pulse">Loading...</div>
    </div>
  );

  const itemsToDisplay = cart?.items || [];

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <div className="max-w-7xl w-full mx-auto flex-1 p-6 md:p-8">
        <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-8 tracking-tight">
          Checkout {product ? `- ${product.name}` : ''}
        </h2>

        <div className="grid lg:grid-cols-2 gap-8">
          {/* Order Summary */}
          <div className="bg-white rounded-2xl shadow-lg p-6 transition-all duration-300 hover:shadow-xl">
            <h3 className="text-2xl font-semibold text-gray-900 mb-6">Order Summary</h3>
            {itemsToDisplay.length > 0 ? (
              <div className="space-y-6">
                {itemsToDisplay.map(item => (
                  <div key={item.productId} className="flex items-center gap-4 border-b border-gray-200 pb-4">
                    <img
                      src={item.imageUrl}
                      alt={item.productName}
                      className="w-20 h-20 object-cover rounded-lg shadow-sm"
                      onError={(e) => (e.target.src = 'https://via.placeholder.com/80')}
                    />
                    <div className="flex-1">
                      <h4 className="text-lg font-medium text-gray-900">{item.productName}</h4>
                      <p className="text-sm text-gray-500">Quantity: {item.quantity}</p>
                      <p className="text-lg font-semibold text-gray-900">₹{item.price * item.quantity}</p>
                    </div>
                  </div>
                ))}
                <div className="flex justify-between text-lg font-semibold text-gray-900 pt-4">
                  <span>Total:</span>
                  <span>₹{cart?.total || 0}</span>
                </div>
              </div>
            ) : (
              <p className="text-gray-500">No items in the cart.</p>
            )}
          </div>

          {/* Shipping & Payment */}
          <div className="bg-white rounded-2xl shadow-lg p-6 transition-all duration-300 hover:shadow-xl">
            <form onSubmit={handleSubmit} className="space-y-8">
              {/* Shipping */}
              <div>
                <h3 className="text-2xl font-semibold text-gray-900 mb-6">Shipping Address</h3>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                  <Input label="Full Name" value={address.fullName} onChange={val => setAddress({ ...address, fullName: val })} required />
                  <Input label="Address Line 1" value={address.line1} onChange={val => setAddress({ ...address, line1: val })} required />
                  <Input label="Address Line 2" value={address.line2} onChange={val => setAddress({ ...address, line2: val })} />
                  <Input label="City" value={address.city} onChange={val => setAddress({ ...address, city: val })} required />
                  <Input label="State" value={address.state} onChange={val => setAddress({ ...address, state: val })} required />
                  <Input label="Postal Code" value={address.postalCode} onChange={val => setAddress({ ...address, postalCode: val })} required />
                  <Input label="Country" value={address.country} onChange={val => setAddress({ ...address, country: val })} required />
                  <Input label="Phone" value={address.phone} onChange={val => setAddress({ ...address, phone: val })} required />
                </div>
              </div>

              {/* Payment */}
              <div>
                <h3 className="text-2xl font-semibold text-gray-900 mb-6">Payment Details</h3>
                <div className="border border-gray-200 rounded-lg p-4 bg-gray-50">
                  <CardElement className="p-2" />
                </div>
              </div>

              <button
                type="submit"
                disabled={!stripe || loading}
                className={`w-full py-3 rounded-lg text-white font-semibold text-lg transition-all duration-200 ${
                  loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700 hover:shadow-md'
                }`}
              >
                {loading ? 'Processing...' : `Pay ₹${cart?.total || 0}`}
              </button>
            </form>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

const Input = ({ label, value, onChange, required = false }) => (
  <div className="flex flex-col">
    <label className="text-sm font-medium text-gray-700 mb-2">
      {label} {required && <span className="text-red-500">*</span>}
    </label>
    <input
      type="text"
      value={value}
      onChange={(e) => onChange(e.target.value)}
      required={required}
      className="border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200"
    />
  </div>
);

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
