import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

function ProductCard({ product }) {
  const navigate = useNavigate();

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      await api.post('/api/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Product added to cart!');
    } catch (err) {
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        console.error('Error adding to cart:', err);
        alert('Something went wrong while adding to cart.');
      }
    }
  };

  if (!product || typeof product !== 'object') {
    console.warn('Invalid product passed to ProductCard:', product);
    return null;
  }

  return (
    <div className="card" style={{ width: '200px', margin: '10px' }}>
      <img
        src={`/api/v1/products/image/${product.imageUrl}`}
        alt={product.name || 'Product'}
        style={{ width: '100%', height: '200px', objectFit: 'cover' }}
      />
      <div className="card-body">
        <h4>{product.name}</h4>
        <p>â‚¹{product.price}</p>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <Link to={`/product/${product.id}`} className="button">View</Link>
          <button className="button" onClick={handleAddToCart}>Add to Cart</button>
        </div>
      </div>
    </div>
  );
}

export default ProductCard;
