import React from 'react';
import { useNavigate } from 'react-router-dom';

function AdminDashboard() {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <div style={{ width: '250px', background: '#f0f0f0', padding: '20px' }}>
        <h3>Admin Panel</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          <li><button onClick={() => navigate('/admin/add-product')}>Add Product</button></li>
          <li><button onClick={() => navigate('/admin/update-product')}>Update Product</button></li>
          <li><button onClick={() => navigate('/admin/remove-product')}>Remove Product</button></li>
          <li><button onClick={() => navigate('/admin/users')}>Access Users</button></li>
          <li><button onClick={() => navigate('/admin/user-orders')}>User Orders</button></li>
        </ul>
      </div>
      <div style={{ flex: 1, padding: '20px' }}>
        <div style={{ textAlign: 'right' }}>
          <button onClick={handleLogout} style={{ backgroundColor: '#ff3f6c', color: '#fff', border: 'none', padding: '10px 20px' }}>
            Logout
          </button>
        </div>
        <h2>Welcome, Admin!</h2>
      </div>
    </div>
  );
}

export default AdminDashboard;
