import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import api from '../services/api';
import Footer from '../components/Footer';
import { toast } from 'react-toastify';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const location = useLocation();
  const { product } = location.state || {};

  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);
  const [address, setAddress] = useState({
    fullName: '',
    line1: '',
    line2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    phone: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (product) {
          const res = await api.get(`/v1/products/get/${product.id}`);
          const normalizeProduct = (data) => ({
            id: data.id,
            name: data.name,
            price: data.price,
            imageUrl: data.imageUrl || data.image || 'https://via.placeholder.com/80',
          });
          const productData = normalizeProduct(res.data);

          setCart({
            items: [{
              productId: productData.id,
              productName: productData.name,
              quantity: 1,
              price: productData.price,
              imageUrl: productData.imageUrl,
            }],
            total: productData.price,
          });
        } else {
          const res = await api.get('/v1/carts');
          const updatedItems = await Promise.all(
            res.data.items.map(async (item) => {
              const productRes = await api.get(`/v1/products/get/${item.productId}`);
              return {
                ...item,
                productName: productRes.data.name,
                imageUrl: productRes.data.imageUrl || productRes.data.image || 'https://via.placeholder.com/80',
              };
            })
          );
          setCart({ ...res.data, items: updatedItems });
        }
      } catch (err) {
        toast.error('Error loading data.');
        console.error('Fetch error:', err);
      }
    };

    fetchData();
  }, [product]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const cardElement = elements.getElement(CardElement);
      if (!stripe || !elements || !cardElement) {
        toast.error('Stripe not ready.');
        setLoading(false);
        return;
      }

      const amount = Math.round(cart.total * 100);
      const cartItems = cart.items.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
      }));

      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        amount,
        currency: 'usd',
        paymentMethodId: 'pm_card_visa',
      });

      const { clientSecret, paymentIntentId } = paymentIntentRes.data;

      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: cardElement },
      });

      if (error) {
        toast.error(`Payment failed: ${error.message}`);
        setLoading(false);
        return;
      }

      const shippingAddress = {
        fullName: address.fullName,
        street: address.line1,
        city: address.city,
        state: address.state,
        zipCode: address.postalCode,
        country: address.country,
        phone: address.phone,
      };

      const checkoutRes = await api.post('/payment/checkout', {
        paymentIntentId: paymentIntent.id,
        shippingAddress,
        items: cartItems,
      });

      toast.success(`Order placed successfully! Order ID: ${checkoutRes.data.orderId}`);
      navigate(`/order/${checkoutRes.data.orderId}`);
    } catch (err) {
      console.error(err);
      toast.error(err.response?.data?.error || 'Checkout failed.');
    } finally {
      setLoading(false);
    }
  };

  if (!cart) return <div className="text-center text-lg py-10">Loading...</div>;

  const itemsToDisplay = cart?.items || [];

  return (
    <div className="max-w-6xl mx-auto min-h-screen flex flex-col px-4">
      <div className="flex-1 py-10">
        <h2 className="text-3xl font-semibold text-gray-800 mb-6">
          Checkout {product ? `- ${product.name}` : ''}
        </h2>

        <div className="bg-white rounded-xl shadow-md p-6 mb-8">
          <h3 className="text-xl font-semibold text-gray-700 mb-4">Order Summary</h3>
          {itemsToDisplay.length > 0 ? (
            itemsToDisplay.map(item => (
              <div key={item.productId} className="flex items-center gap-4 mb-4 border-b pb-4">
                <img
                  src={item.imageUrl}
                  alt={item.productName}
                  className="w-20 h-20 object-cover rounded-md"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/80';
                  }}
                />
                <div className="flex-1">
                  <h4 className="text-base font-semibold">{item.productName}</h4>
                  <p className="text-sm text-gray-600">Qty: {item.quantity}</p>
                  <p className="text-sm font-semibold">₹{item.price * item.quantity}</p>
                </div>
              </div>
            ))
          ) : (
            <p>No items to checkout.</p>
          )}
          <div className="flex justify-between font-semibold text-lg mt-4">
            <span>Total:</span>
            <span>₹{cart?.total || 0}</span>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="bg-white rounded-xl shadow-md p-6">
          <h3 className="text-xl font-semibold text-gray-700 mb-4">Shipping Address</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
            <div>
              <label className="font-medium">Full Name</label>
              <input
                type="text"
                required
                value={address.fullName}
                onChange={(e) => setAddress({ ...address, fullName: e.target.value })}
                className="input-style"
              />
            </div>
            <div>
              <label className="font-medium">Line 1</label>
              <input
                type="text"
                required
                value={address.line1}
                onChange={(e) => setAddress({ ...address, line1: e.target.value })}
                className="input-style"
              />
            </div>
            <div>
              <label className="font-medium">Line 2</label>
              <input
                type="text"
                value={address.line2}
                onChange={(e) => setAddress({ ...address, line2: e.target.value })}
                className="input-style"
              />
            </div>
            <div>
              <label className="font-medium">City</label>
              <input
                type="text"
                required
                value={address.city}
                onChange={(e) => setAddress({ ...address, city: e.target.value })}
                className="input-style"
              />
            </div>
            <div>
              <label className="font-medium">State</label>
              <input
                type="text"
                required
                value={address.state}
                onChange={(e) => setAddress({ ...address, state: e.target.value })}
                className="input-style"
              />
            </div>
            <div>
              <label className="font-medium">Postal Code</label>
              <input
                type="text"
                required
                value={address.postalCode}
                onChange={(e) => setAddress({ ...address, postalCode: e.target.value })}
                className="input-style"
              />
            </div>
          </div>
          <div className="mb-4">
            <label className="font-medium">Country</label>
            <input
              type="text"
              required
              value={address.country}
              onChange={(e) => setAddress({ ...address, country: e.target.value })}
              className="input-style"
            />
          </div>
          <div className="mb-6">
            <label className="font-medium">Phone</label>
            <input
              type="tel"
              required
              value={address.phone}
              onChange={(e) => setAddress({ ...address, phone: e.target.value })}
              className="input-style"
            />
          </div>

          <h3 className="text-xl font-semibold text-gray-700 mb-4">Payment Details</h3>
          <div className="p-4 border rounded-md mb-6">
            <CardElement />
          </div>

          <button
            type="submit"
            disabled={!stripe || loading}
            className={`w-full py-3 rounded-md text-white ${
              loading ? 'bg-blue-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'
            }`}
          >
            {loading ? 'Processing...' : `Pay ₹${cart?.total || 0}`}
          </button>
        </form>
      </div>

      <div className="mt-auto">
        <Footer />
      </div>
    </div>
  );
}

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
