import React from 'react';
import { useNavigate } from 'react-router-dom';
import './CategoryBanner.css';

const categories = [
  {
    name: 'T-shirts',
    image: 'https://via.placeholder.com/300x180?text=T-shirts',
  },
  {
    name: 'Jeans',
    image: 'https://via.placeholder.com/300x180?text=Jeans',
  }
];

function CategoryBanner() {
  const navigate = useNavigate();

  const handleClick = (name) => {
    navigate(`/category-products?name=${encodeURIComponent(name)}`);
  };

  return (
    <div className="category-banner">
      {categories.map((cat) => (
        <div key={cat.name} className="banner-item" onClick={() => handleClick(cat.name)}>
          <img src={cat.image} alt={cat.name} className="banner-img" />
          <h3>{cat.name}</h3>
        </div>
      ))}
    </div>
  );
}

export default CategoryBanner;
