import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    fetchProduct();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/get/${id}`);
      setProduct(res.data);
    } catch (err) {
      console.error('Failed to fetch product:', err);
    }
  };

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      // if no token, redirect to login
      navigate('/login');
      return;
    }

    try {
      await api.post('/v1/carts/items', { productId: product.id, quantity });
      // After adding to cart, go to cart page
      navigate('/cart');
    } catch (err) {
      console.error('Add to cart error:', err);
      alert('Failed to add item to cart. Please try again.');
    }
  };

  const handleBuyNow = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    // First add to cart, then navigate to checkout
    api
      .post('/v1/carts/items', { productId: product.id, quantity })
      .then(() => {
        navigate('/checkout');
      })
      .catch((err) => {
        console.error('Buy Now error:', err);
        alert('Failed to proceed to checkout. Please try again.');
      });
  };

  if (!product) return <div>Loading...</div>;

  return (
    <div className="container" style={{ display: 'flex', marginTop: '20px' }}>
      <img
        src={product.imageUrl}
        alt={product.name}
        style={{ width: '400px', height: '400px', objectFit: 'cover' }}
      />
      <div style={{ marginLeft: '20px' }}>
        <h2>{product.name}</h2>
        <p>{product.description}</p>
        <p>
          <strong>Price:</strong> â‚¹{product.price}
        </p>
        <p>
          <strong>Brand:</strong> {product.brand}
        </p>
        <div>
          <label>Quantity:</label>
          <input
            type="number"
            value={quantity}
            min="1"
            onChange={(e) => setQuantity(Number(e.target.value))}
          />
        </div>
        <button
          className="button"
          onClick={handleAddToCart}
          style={{ marginTop: '10px' }}
        >
          Add to Cart
        </button>
        <button
          className="button"
          onClick={handleBuyNow}
          style={{ marginTop: '10px', marginLeft: '10px' }}
        >
          Buy Now
        </button>
      </div>
    </div>
  );
}

export default ProductDetail;
