import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

function ProductCard({ product }) {
  const navigate = useNavigate();

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      await api.post('/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      });
      alert('Product added to cart!');
    } catch (err) {
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        console.error('Error adding to cart:', err);
        alert('Something went wrong while adding to cart.');
      }
    }
  };

  if (!product || typeof product !== 'object') {
    console.warn('Invalid product passed to ProductCard:', product);
    return null;
  }

  return (
    <div
      style={{
        border: '1px solid #e0e0e0',
        borderRadius: '4px',
        background: 'white',
        padding: '10px',
        margin: '10px',
        width: '200px',
      }}
    >
      <Link to={`/product/${product.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
        <img
          src={`http://localhost:8080/api/v1/products/image/${product.imageUrl}`}
          alt={product.name}
          style={{ width: '100%', height: '200px', objectFit: 'cover' }}
        />
        <h3 style={{ fontSize: '16px', margin: '10px 0' }}>{product.name}</h3>
        <p style={{ color: '#666' }}>â‚¹{product.price}</p>
      </Link>
      <button className="button" onClick={handleAddToCart} style={{ marginTop: '10px', width: '100%' }}>
        Add to Cart
      </button>
    </div>
  );
}

export default ProductCard;
