import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function Register() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = { name, email, password, role: 'CUSTOMER' };
      const res = await api.post('/auth/register', payload);
      console.log('Register successful:', res.data);
      navigate('/login');
    } catch (err) {
      console.error('Registration failed:', err.response ? err.response.data : err);
      alert(err.response?.data?.message || 'Registration failed. Check console.');
    }
  };

  const handleGoogleRegister = () => {
    // Replace with actual Google auth flow
    window.location.href = 'http://localhost:8080/oauth2/authorization/google'; // Adjust to your backend
  };

  return (
    <div className="container" style={{ maxWidth: '400px', marginTop: '50px' }}>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        </div>
        <button className="button" type="submit" style={{ marginTop: '10px', width: '100%' }}>
          Register
        </button>
      </form>

      <div style={{ textAlign: 'center', margin: '20px 0' }}>— or —</div>
      <button onClick={handleGoogleRegister} className="button" style={{ width: '100%', background: '#4285F4', color: 'white' }}>
        Continue with Google
      </button>
    </div>
  );
}

export default Register;
