import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [similarProducts, setSimilarProducts] = useState([]);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchProduct();
  }, [id]); // re-fetch if URL id changes

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/get/${id}`);
      setProduct(res.data);
      fetchSimilarProducts(res.data.category, res.data.id); // fetch similar products by category
    } catch (err) {
      console.error('Error fetching product:', err);
      setError('‚ùå Product not found');
    }
  };

  const fetchSimilarProducts = async (categoryName, productId) => {
    try {
      const res = await api.get(`/v1/products/category/byname?name=${encodeURIComponent(categoryName)}`);
      const filtered = res.data.filter((p) => p.id !== parseInt(productId));
      setSimilarProducts(filtered);
    } catch (err) {
      console.error('Error fetching similar products:', err);
    }
  };

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      await api.post('/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      });
      setMessage('‚úîÔ∏è Product added to cart!');
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      console.error('Add to cart error:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setMessage('‚ùå Failed to add to cart.');
      }
    }
  };

  if (error) {
    return <div style={{ padding: '20px', color: 'red' }}>{error}</div>;
  }

  if (!product) {
    return <div style={{ padding: '20px' }}>Loading...</div>;
  }

  return (
    <div style={{ padding: '40px' }}>
      {/* Product Info */}
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '40px' }}>
        <img
          src={product.imageUrl || '/placeholder.png'}
          onError={(e) => { e.target.src = '/placeholder.png'; }}
          alt={product.name}
          style={{
            width: '300px',
            height: '300px',
            objectFit: 'cover',
            borderRadius: '8px',
            boxShadow: '0 0 10px rgba(0,0,0,0.1)',
          }}
        />
        <div style={{ maxWidth: '500px' }}>
          <h2>{product.name}</h2>
          <p style={{ fontSize: '20px', fontWeight: 'bold' }}>‚Çπ{product.price}</p>
          <p>{product.description}</p>
          <p><strong>Brand:</strong> {product.brand}</p>
          <p><strong>Category:</strong> {product.category}</p>

          <button className="button" onClick={handleAddToCart}>
            Add to Cart
          </button>
          <button
            className="button"
            onClick={() => navigate('/')}
            style={{ marginLeft: '10px' }}
          >
            ‚¨Ö Back to Home
          </button>

          {message && (
            <p style={{ marginTop: '10px', color: message.includes('‚úîÔ∏è') ? 'green' : 'red' }}>
              {message}
            </p>
          )}
        </div>
      </div>

      {/* Similar Products */}
      {similarProducts.length > 0 && (
        <div style={{ marginTop: '60px' }}>
          <h3 style={{ marginBottom: '20px' }}>üîÅ Similar Products</h3>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
            {similarProducts.map((item) => (
              <div
                key={item.id}
                style={{
                  width: '180px',
                  padding: '10px',
                  border: '1px solid #eee',
                  borderRadius: '8px',
                  textAlign: 'center',
                  boxShadow: '0 0 6px rgba(0,0,0,0.05)',
                  cursor: 'pointer',
                }}
                onClick={() => navigate(`/product/${item.id}`)}
              >
                <img
                  src={item.imageUrl || '/placeholder.png'}
                  onError={(e) => { e.target.src = '/placeholder.png'; }}
                  alt={item.name}
                  style={{
                    width: '100%',
                    height: '150px',
                    objectFit: 'cover',
                    borderRadius: '6px',
                  }}
                />
                <h4 style={{ fontSize: '16px', margin: '10px 0 5px' }}>{item.name}</h4>
                <p style={{ color: 'green', fontWeight: 'bold' }}>‚Çπ{item.price}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default ProductDetail;
