import React from 'react';
import { useNavigate } from 'react-router-dom';
import './CategoryStrip.css'; // custom styles

const categories = [
  { name: 'Jeans', image: '/images/CSjeans.png' },
  { name: 'T-shirts', image: '/images/CStshirt.png' },
  { name: 'Shoes', image: '/images/CSshoes.png' },
  { name: 'Jackets', image: '/images/CSjackets.png' },
  { name: 'Sunglasses', image: '/images/CSsunglasses.png' },
  { name: 'Caps', image: '/images/CScaps.png' },
  { name: 'Shirts', image: '/images/CSshirts.png' },
];

function CategoryStrip() {
  const navigate = useNavigate();

  const handleClick = (name) => {
    navigate(`/category/byname?name=${name}`);
  };

  return (
    <div className="category-strip">
      {categories.map((cat) => (
        <div key={cat.name} className="category-item" onClick={() => handleClick(cat.name)}>
          <img src={cat.image} alt={cat.name} />
          <p>{cat.name}</p>
        </div>
      ))}
    </div>
  );
}

export default CategoryStrip;
