import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { Link, useNavigate } from 'react-router-dom';

function Cart() {
  const [cart, setCart] = useState({ items: [], total: 0 });
  const navigate = useNavigate();

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      const res = await api.get('/v1/carts');
      setCart(res.data);
    } catch (err) {
      console.error('Error fetching cart:', err);
      if (err.response?.status === 401) {
        navigate('/login');
      }
    }
  };

  const handleRemove = async (itemId) => {
    try {
      await api.delete(`/v1/carts/remove/${itemId}`);
      fetchCart(); // Refresh cart
    } catch (err) {
      console.error('Error removing item:', err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Shopping Cart</h2>
      {cart.items.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          {cart.items.map((item) => {
            const product = item.product;
            const productId = product?.id;

            // Skip item if product or product.id is missing
            if (!productId) {
              console.warn('Skipping cart item with missing product:', item);
              return null;
            }

            return (
              <div key={item.itemId} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px', borderRadius: '8px' }}>
                <Link to={`/product/${productId}`} style={{ textDecoration: 'none', color: 'black' }}>
                  <div style={{ display: 'flex', alignItems: 'center' }}>
                    <img
                      src={product.imageUrl}
                      alt={product.name}
                      style={{ width: '100px', height: '100px', objectFit: 'cover', marginRight: '20px', borderRadius: '6px' }}
                    />
                    <div>
                      <h3>{product.name}</h3>
                      <p>Price: ₹{product.price}</p>
                      <p>Quantity: {item.quantity}</p>
                    </div>
                  </div>
                </Link>
                <button
                  onClick={() => handleRemove(item.itemId)}
                  style={{ marginTop: '10px', padding: '6px 12px', background: 'red', color: 'white', border: 'none', borderRadius: '4px' }}
                >
                  Remove
                </button>
              </div>
            );
          })}
          <h3>Total: ₹{cart.total}</h3>
          <button
            onClick={() => navigate('/checkout')}
            style={{ marginTop: '20px', padding: '10px 20px', background: 'green', color: 'white', border: 'none', borderRadius: '6px' }}
          >
            Proceed to Checkout
          </button>
        </>
      )}
    </div>
  );
}

export default Cart;
