import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../services/api';

function Cart() {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const fetchCart = async () => {
    setLoading(true);
    setError('');

    try {
      const res = await api.get('/v1/carts');
      setCart(res.data);
    } catch (err) {
      if (err.response?.status === 401) {
        console.warn('Unauthorized - token might be invalid');
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        console.error(err);
        setError('Failed to load cart. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const handleRemove = async (itemId) => {
    try {
      await api.delete(`/v1/carts/items/${itemId}`);
      await fetchCart();
    } catch (err) {
      console.error(err);
      setError('Failed to remove item. Please try again.');
    }
  };

  const handleClear = async () => {
    try {
      await api.delete('/v1/carts');
      setCart({ items: [], total: 0 });
    } catch (err) {
      console.error(err);
      setError('Failed to clear cart. Please try again.');
    }
  };

  const handleUpdateQuantity = async (itemId, quantity) => {
    if (quantity < 1) return;
    try {
      await api.put(`/v1/carts/items/${itemId}`, { quantity });
      await fetchCart();
    } catch (err) {
      console.error(err);
      setError('Failed to update quantity. Please try again.');
    }
  };

  if (loading) return <div>Loading cart...</div>;

  if (error) {
    return (
      <div style={{ padding: '20px', color: 'red' }}>
        <p>{error}</p>
      </div>
    );
  }

  if (!cart || !cart.items) return <div>No cart data.</div>;

  return (
    <div className="container">
      <h2>Your Cart</h2>
      {cart.items.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          {cart.items.map((item) => (
            <div
              key={item.itemId}
              style={{
                display: 'flex',
                alignItems: 'center',
                marginBottom: '10px',
                background: 'white',
                padding: '10px',
                borderRadius: '4px',
              }}
            >
              <Link to={`/product/${item.product?.id}`}>
                <img
                  src={
                    item.product?.imageUrl
                      ? `http://localhost:8080/api/v1/products/image/${item.product.imageUrl}`
                      : '/placeholder.png'
                  }
                  alt={item.product?.name || 'Product Image'}
                  style={{ width: '100px', height: '100px', objectFit: 'cover' }}
                  onError={(e) => {
                    e.target.src = '/placeholder.png';
                  }}
                />
              </Link>

              <div style={{ marginLeft: '20px', flex: 1 }}>
                <Link
                  to={`/product/${item.product?.id}`}
                  style={{ textDecoration: 'none', color: 'black' }}
                >
                  <h4>{item.product?.name || 'Unknown Product'}</h4>
                </Link>
                <p>Price: ₹{item.product?.price || 'N/A'}</p>
                <p>Subtotal: ₹{item.subtotal || 'N/A'}</p>
                <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                  <button
                    className="button"
                    onClick={() => handleUpdateQuantity(item.itemId, item.quantity - 1)}
                    disabled={item.quantity <= 1}
                  >
                    -
                  </button>
                  <span>Qty: {item.quantity}</span>
                  <button
                    className="button"
                    onClick={() => handleUpdateQuantity(item.itemId, item.quantity + 1)}
                  >
                    +
                  </button>
                  <button
                    className="button"
                    onClick={() => handleRemove(item.itemId)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            </div>
          ))}
          <h3>Total: ₹{cart.total || 0}</h3>
          <button
            className="button"
            onClick={() => navigate('/checkout')}
            style={{ marginTop: '10px' }}
          >
            Proceed to Checkout
          </button>
          <button
            className="button"
            onClick={handleClear}
            style={{ marginTop: '10px', marginLeft: '10px' }}
          >
            Clear Cart
          </button>
        </div>
      )}
    </div>
  );
}

export default Cart;
