import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import api from '../services/api';
import ProductCard from '../components/ProductCard';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [similarProducts, setSimilarProducts] = useState([]);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/get/${id}`);
      setProduct(res.data);
      if (res.data?.category?.name) {
        fetchSimilarProducts(res.data.category.name, res.data.id);
      }
    } catch (err) {
      console.error('Error fetching product:', err);
      setError('❌ Product not found');
    }
  };

  const fetchSimilarProducts = async (categoryName, currentProductId) => {
    try {
      const res = await api.get(`/v1/products/category/byname?name=${encodeURIComponent(categoryName)}`);
      if (Array.isArray(res.data)) {
        const filteredProducts = res.data.filter(p => p.id !== parseInt(currentProductId));
        setSimilarProducts(filteredProducts);
      } else {
        console.error('Expected array but got:', res.data);
        setSimilarProducts([]);
      }
    } catch (err) {
      console.error('Error fetching similar products:', err);
      setSimilarProducts([]);
    }
  };

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      await api.post('/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      });
      setMessage('✔️ Product added to cart!');
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      console.error('Add to cart error:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setMessage('❌ Failed to add to cart.');
      }
    }
  };

  if (error) {
    return <div style={{ padding: '20px', color: 'red' }}>{error}</div>;
  }

  if (!product) {
    return <div style={{ padding: '20px' }}>Loading...</div>;
  }

  return (
    <div style={{ padding: '40px', display: 'flex', flexDirection: 'column', gap: '40px' }}>
      {/* Product Details Section */}
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '40px' }}>
        <img
          src={product.imageUrl || '/placeholder.png'}
          onError={(e) => { e.target.src = '/placeholder.png'; }}
          alt={product.name}
          style={{
            width: '300px',
            height: '300px',
            objectFit: 'cover',
            borderRadius: '8px',
            boxShadow: '0 0 10px rgba(0,0,0,0.1)',
          }}
        />
        <div style={{ maxWidth: '500px' }}>
          <h2>{product.name}</h2>
          <p style={{ fontSize: '20px', fontWeight: 'bold' }}>₹{product.price}</p>
          <p>{product.description || 'No description available'}</p>
          <p><strong>Brand:</strong> {product.brand || 'N/A'}</p>
          <p><strong>Category:</strong> {product.category?.name || 'N/A'}</p>

          {product.category?.name && (
            <p>
              <Link to={`/category/${product.category.name}`}>
                View more in {product.category.name}
              </Link>
            </p>
          )}

          <button className="button" onClick={handleAddToCart}>
            Add to Cart
          </button>
          <button
            className="button"
            onClick={() => navigate('/')}
            style={{ marginLeft: '10px' }}
          >
            ⬅ Back to Home
          </button>

          {message && (
            <p style={{ marginTop: '10px', color: message.includes('✔️') ? 'green' : 'red' }}>
              {message}
            </p>
          )}
        </div>
      </div>

      {/* Similar Products Section */}
      <div>
        <h3 style={{ fontSize: '24px', margin: '20px 0' }}>Similar Products</h3>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
          {similarProducts.length > 0 ? (
            similarProducts.map(product => (
              <ProductCard key={product.id} product={product} />
            ))
          ) : (
            <p>No similar products found.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
