import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { useNavigate } from 'react-router-dom';

function Cart() {
  const [cart, setCart] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchCart();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchCart = async () => {
    try {
      const res = await api.get('/v1/carts');
      setCart(res.data);
    } catch (err) {
      console.error('Failed to fetch cart:', err);
      // If this returns 401/403, you might want to force logout or redirect to login.
      // e.g. navigate('/login');
    }
  };

  const handleRemove = async (itemId) => {
    try {
      await api.delete(`/v1/carts/items/${itemId}`);
      fetchCart();
    } catch (err) {
      console.error('Remove item error:', err);
      alert('Failed to remove item. Please try again.');
    }
  };

  const handleUpdate = async (itemId, quantity) => {
    try {
      await api.put(`/v1/carts/items/${itemId}`, { quantity });
      fetchCart();
    } catch (err) {
      console.error('Update quantity error:', err);
      alert('Failed to update quantity. Please try again.');
    }
  };

  const handleClear = async () => {
    try {
      await api.delete('/v1/carts');
      setCart({ items: [], total: 0 });
    } catch (err) {
      console.error('Clear cart error:', err);
      alert('Failed to clear cart. Please try again.');
    }
  };

  if (!cart) return <div>Loading...</div>;

  return (
    <div className="container">
      <h2>Your Cart</h2>
      {cart.items.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          {cart.items.map((item) => (
            <div
              key={item.itemId}
              style={{
                display: 'flex',
                alignItems: 'center',
                marginBottom: '10px',
                background: 'white',
                padding: '10px',
                borderRadius: '4px',
              }}
            >
              <img
                src={item.product.imageUrl}
                alt={item.productName}
                style={{ width: '100px', height: '100px', objectFit: 'cover' }}
              />
              <div style={{ marginLeft: '20px', flex: 1 }}>
                <h4>{item.productName}</h4>
                <p>Price: â‚¹{item.price}</p>
                <p>Subtotal: â‚¹{item.subtotal}</p>
                <div>
                  <label>Quantity:</label>
                  <input
                    type="number"
                    value={item.quantity}
                    min="1"
                    onChange={(e) =>
                      handleUpdate(item.itemId, Number(e.target.value))
                    }
                  />
                </div>
                <button
                  className="button"
                  onClick={() => handleRemove(item.itemId)}
                  style={{ marginTop: '10px' }}
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
          <h3>Total: â‚¹{cart.total}</h3>
          <button
            className="button"
            onClick={() => navigate('/checkout')}
            style={{ marginTop: '10px' }}
          >
            Proceed to Checkout
          </button>
          <button
            className="button"
            onClick={handleClear}
            style={{ marginTop: '10px', marginLeft: '10px' }}
          >
            Clear Cart
          </button>
        </div>
      )}
    </div>
  );
}

export default Cart;
