import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function ProductDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchProduct();
  }, [id]); // update when route param changes

  const fetchProduct = async () => {
    try {
      const res = await api.get(`/v1/products/get/${id}`);
      setProduct(res.data);
      fetchCategoryProducts(res.data.category, res.data.id);
    } catch (err) {
      console.error('Error fetching product:', err);
      setError('❌ Product not found');
    }
  };

  const fetchCategoryProducts = async (categoryName, currentProductId) => {
    try {
      const res = await api.get(`/v1/products/category/byname`, {
        params: { name: categoryName }
      });
      const filtered = res.data.filter(p => p.id !== currentProductId);
      setRelatedProducts(filtered);
    } catch (err) {
      console.error('Error fetching related products:', err);
    }
  };

  const handleAddToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      await api.post('/v1/carts/items', {
        productId: product.id,
        quantity: 1,
      });
      setMessage('✔️ Product added to cart!');
      setTimeout(() => setMessage(''), 3000);
    } catch (err) {
      console.error('Add to cart error:', err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        navigate('/login');
      } else {
        setMessage('❌ Failed to add to cart.');
      }
    }
  };

  if (error) {
    return <div style={{ padding: '20px', color: 'red' }}>{error}</div>;
  }

  if (!product) {
    return <div style={{ padding: '20px' }}>Loading...</div>;
  }

  return (
    <div style={{ padding: '40px' }}>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '40px' }}>
        <img
          src={product.imageUrl || '/placeholder.png'}
          onError={(e) => { e.target.src = '/placeholder.png'; }}
          alt={product.name}
          style={{
            width: '300px',
            height: '300px',
            objectFit: 'cover',
            borderRadius: '8px',
            boxShadow: '0 0 10px rgba(0,0,0,0.1)',
          }}
        />
        <div style={{ maxWidth: '500px' }}>
          <h2>{product.name}</h2>
          <p style={{ fontSize: '20px', fontWeight: 'bold' }}>₹{product.price}</p>
          <p>{product.description}</p>
          <p><strong>Brand:</strong> {product.brand}</p>
          <p><strong>Category:</strong> {product.category}</p>

          <button className="button" onClick={handleAddToCart}>
            Add to Cart
          </button>
          <button
            className="button"
            onClick={() => navigate('/')}
            style={{ marginLeft: '10px' }}
          >
            ⬅ Back to Home
          </button>

          {message && (
            <p style={{ marginTop: '10px', color: message.includes('✔️') ? 'green' : 'red' }}>
              {message}
            </p>
          )}
        </div>
      </div>

      {relatedProducts.length > 0 && (
        <div style={{ marginTop: '60px' }}>
          <h3>Related Products in {product.category}</h3>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', marginTop: '20px' }}>
            {relatedProducts.map(p => (
              <div
                key={p.id}
                onClick={() => navigate(`/product/${p.id}`)}
                style={{
                  width: '150px',
                  cursor: 'pointer',
                  padding: '10px',
                  border: '1px solid #ccc',
                  borderRadius: '8px',
                  textAlign: 'center',
                }}
              >
                <img
                  src={p.imageUrl || '/placeholder.png'}
                  alt={p.name}
                  onError={(e) => { e.target.src = '/placeholder.png'; }}
                  style={{ width: '100%', height: '150px', objectFit: 'cover' }}
                />
                <p style={{ fontWeight: 'bold', marginTop: '8px' }}>{p.name}</p>
                <p style={{ color: 'gray' }}>₹{p.price}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default ProductDetail;
