import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import api from '../services/api';
import Footer from '../components/Footer';
import { toast } from 'react-toastify';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const location = useLocation();
  const { product } = location.state || {};

  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(false);
  const [address, setAddress] = useState({
    fullName: '',
    line1: '',
    line2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    phone: '',
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (product) {
          const res = await api.get(`/v1/products/get/${product.id}`);
          const normalizeProduct = (data) => ({
            id: data.id,
            name: data.name,
            price: data.price,
            imageUrl: data.imageUrl || data.image || 'https://via.placeholder.com/80',
          });
          const productData = normalizeProduct(res.data);

          setCart({
            items: [{
              productId: productData.id,
              productName: productData.name,
              quantity: 1,
              price: productData.price,
              imageUrl: productData.imageUrl,
            }],
            total: productData.price,
          });
        } else {
          const res = await api.get('/v1/carts');
          const updatedItems = await Promise.all(
            res.data.items.map(async (item) => {
              const productRes = await api.get(`/v1/products/get/${item.productId}`);
              return {
                ...item,
                productName: productRes.data.name,
                imageUrl: productRes.data.imageUrl || productRes.data.image || 'https://via.placeholder.com/80',
              };
            })
          );
          setCart({ ...res.data, items: updatedItems });
        }
      } catch (err) {
        toast.error('Error loading data.');
      }
    };
    fetchData();
  }, [product]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const cardElement = elements.getElement(CardElement);
      if (!stripe || !elements || !cardElement) {
        toast.error('Stripe not ready.');
        setLoading(false);
        return;
      }

      const amount = Math.round(cart.total * 100);
      const cartItems = cart.items.map(item => ({
        productId: item.productId,
        quantity: item.quantity,
      }));

      const paymentIntentRes = await api.post('/payment/create-payment-intent', {
        amount,
        currency: 'usd',
        paymentMethodId: 'pm_card_visa',
      });

      const { clientSecret, paymentIntentId } = paymentIntentRes.data;

      const { error, paymentIntent } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { card: cardElement },
      });

      if (error) {
        toast.error(`Payment failed: ${error.message}`);
        setLoading(false);
        return;
      }

      const shippingAddress = {
        fullName: address.fullName,
        street: address.line1,
        city: address.city,
        state: address.state,
        zipCode: address.postalCode,
        country: address.country,
        phone: address.phone,
      };

      const checkoutRes = await api.post('/payment/checkout', {
        paymentIntentId: paymentIntent.id,
        shippingAddress,
        items: cartItems,
      });

      toast.success(`Order placed! Order ID: ${checkoutRes.data.orderId}`);
      navigate(`/order/${checkoutRes.data.orderId}`);
    } catch (err) {
      console.error(err);
      toast.error(err.response?.data?.error || 'Checkout failed.');
    } finally {
      setLoading(false);
    }
  };

  if (!cart) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50 flex items-center justify-center">
        <div className="bg-white rounded-2xl shadow-lg p-8 text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-lg text-gray-600">Loading your order...</p>
        </div>
      </div>
    );
  }

  const itemsToDisplay = cart?.items || [];

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-slate-50 to-blue-50">
      <div className="max-w-7xl w-full mx-auto flex-1 p-6">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-800 mb-2">
            Secure Checkout
          </h1>
          {product && (
            <p className="text-xl text-gray-600">Complete your purchase of {product.name}</p>
          )}
          <div className="w-24 h-1 bg-gradient-to-r from-blue-500 to-purple-500 mx-auto mt-4 rounded-full"></div>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Order Summary */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-2xl shadow-xl border border-gray-100 overflow-hidden sticky top-6">
              <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-6">
                <h3 className="text-2xl font-bold flex items-center">
                  <svg className="w-6 h-6 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                  </svg>
                  Order Summary
                </h3>
              </div>

              <div className="p-6">
                {itemsToDisplay.length > 0 ? (
                  <div className="space-y-4">
                    {itemsToDisplay.map(item => (
                      <div key={item.productId} className="flex items-center gap-4 p-4 bg-gray-50 rounded-xl border border-gray-100 hover:shadow-md transition-shadow">
                        <div className="relative">
                          <img
                            src={item.imageUrl}
                            alt={item.productName}
                            className="w-16 h-16 object-cover rounded-lg shadow-sm"
                            onError={(e) => (e.target.src = 'https://via.placeholder.com/80')}
                          />
                          <div className="absolute -top-2 -right-2 bg-blue-600 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center font-bold">
                            {item.quantity}
                          </div>
                        </div>
                        <div className="flex-1">
                          <h4 className="font-semibold text-gray-800 mb-1">{item.productName}</h4>
                          <p className="text-sm text-gray-500 mb-2">Quantity: {item.quantity}</p>
                          <p className="font-bold text-blue-600 text-lg">â‚¹{item.price * item.quantity}</p>
                        </div>
                      </div>
                    ))}

                    <div className="border-t border-gray-200 pt-4 mt-6">
                      <div className="bg-gradient-to-r from-blue-50 to-purple-50 rounded-xl p-4">
                        <div className="flex justify-between items-center">
                          <span className="text-xl font-bold text-gray-800">Total Amount:</span>
                          <span className="text-2xl font-bold text-blue-600">â‚¹{cart?.total || 0}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <svg className="w-16 h-16 text-gray-300 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z" />
                    </svg>
                    <p className="text-gray-500">No items in the cart.</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Shipping & Payment Form */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-2xl shadow-xl border border-gray-100 overflow-hidden">
              <form onSubmit={handleSubmit} className="space-y-8">
                {/* Shipping Address */}
                <div className="p-8 border-b border-gray-100">
                  <div className="flex items-center mb-6">
                    <div className="bg-blue-100 rounded-full p-3 mr-4">
                      <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                      </svg>
                    </div>
                    <h3 className="text-2xl font-bold text-gray-800">Shipping Address</h3>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="md:col-span-2">
                      <Input 
                        label="Full Name" 
                        value={address.fullName} 
                        onChange={val => setAddress({ ...address, fullName: val })} 
                        required 
                        icon="ðŸ‘¤"
                      />
                    </div>
                    <Input 
                      label="Address Line 1" 
                      value={address.line1} 
                      onChange={val => setAddress({ ...address, line1: val })} 
                      required 
                      icon="ðŸ "
                    />
                    <Input 
                      label="Address Line 2" 
                      value={address.line2} 
                      onChange={val => setAddress({ ...address, line2: val })} 
                      icon="ðŸ "
                    />
                    <Input 
                      label="City" 
                      value={address.city} 
                      onChange={val => setAddress({ ...address, city: val })} 
                      required 
                      icon="ðŸ™ï¸"
                    />
                    <Input 
                      label="State" 
                      value={address.state} 
                      onChange={val => setAddress({ ...address, state: val })} 
                      required 
                      icon="ðŸ—ºï¸"
                    />
                    <Input 
                      label="Postal Code" 
                      value={address.postalCode} 
                      onChange={val => setAddress({ ...address, postalCode: val })} 
                      required 
                      icon="ðŸ“®"
                    />
                    <Input 
                      label="Country" 
                      value={address.country} 
                      onChange={val => setAddress({ ...address, country: val })} 
                      required 
                      icon="ðŸŒ"
                    />
                    <Input 
                      label="Phone Number" 
                      value={address.phone} 
                      onChange={val => setAddress({ ...address, phone: val })} 
                      required 
                      icon="ðŸ“±"
                    />
                  </div>
                </div>

                {/* Payment Details */}
                <div className="p-8">
                  <div className="flex items-center mb-6">
                    <div className="bg-green-100 rounded-full p-3 mr-4">
                      <svg className="w-6 h-6 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                      </svg>
                    </div>
                    <h3 className="text-2xl font-bold text-gray-800">Payment Details</h3>
                    <div className="ml-auto flex space-x-2">
                      <div className="w-8 h-5 bg-blue-600 rounded text-white text-xs flex items-center justify-center font-bold">VISA</div>
                      <div className="w-8 h-5 bg-red-600 rounded text-white text-xs flex items-center justify-center font-bold">MC</div>
                      <div className="w-8 h-5 bg-blue-500 rounded text-white text-xs flex items-center justify-center font-bold">AMEX</div>
                    </div>
                  </div>

                  <div className="bg-gray-50 border-2 border-gray-200 rounded-xl p-6 focus-within:border-blue-500 focus-within:bg-white transition-all">
                    <CardElement
                      options={{
                        style: {
                          base: {
                            fontSize: '16px',
                            color: '#424770',
                            '::placeholder': {
                              color: '#aab7c4',
                            },
                            fontFamily: 'system-ui, -apple-system, sans-serif',
                          },
                          invalid: {
                            color: '#9e2146',
                          },
                        },
                      }}
                    />
                  </div>

                  <div className="mt-6 p-4 bg-blue-50 rounded-xl border border-blue-200">
                    <div className="flex items-center text-sm text-blue-800">
                      <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
                      </svg>
                      Your payment information is encrypted and secure
                    </div>
                  </div>
                </div>

                {/* Submit Button */}
                <div className="px-8 pb-8">
                  <button
                    type="submit"
                    disabled={!stripe || loading}
                    className={`w-full py-4 rounded-xl text-white font-bold text-lg transition-all transform ${
                      loading 
                        ? 'bg-gray-400 cursor-not-allowed' 
                        : 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 hover:scale-105 shadow-lg hover:shadow-xl'
                    }`}
                  >
                    {loading ? (
                      <div className="flex items-center justify-center">
                        <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                        Processing Payment...
                      </div>
                    ) : (
                      <div className="flex items-center justify-center">
                        <svg className="w-5 h-5 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
                        </svg>
                        Complete Payment - â‚¹{cart?.total || 0}
                      </div>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

const Input = ({ label, value, onChange, required = false, icon }) => (
  <div className="flex flex-col">
    <label className="text-sm font-semibold mb-2 text-gray-700 flex items-center">
      {icon && <span className="mr-2">{icon}</span>}
      {label} 
      {required && <span className="text-red-500 ml-1">*</span>}
    </label>
    <input
      type="text"
      value={value}
      onChange={(e) => onChange(e.target.value)}
      required={required}
      className="border-2 border-gray-200 rounded-xl px-4 py-3 focus:outline-none focus:border-blue-500 focus:bg-white bg-gray-50 transition-all hover:border-gray-300"
      placeholder={`Enter your ${label.toLowerCase()}`}
    />
  </div>
);

export default function Checkout() {
  return (
    <Elements stripe={stripePromise}>
      <CheckoutForm />
    </Elements>
  );
}
